INSERT OR REPLACE INTO LocalizedText (Language, Tag, Text)
VALUES
--NETHERLANDS 荷兰 LEADER_WILLIAM_OF_ORANGE
("zh_Hans_CN",	"LOC_TRAIT_LEADER_WILLIAM_OF_ORANGE_DESCRIPTION",		"建造港口可触发文化炸弹。每个造船厂+1 [ICON_TradeRoute] 贸易路线容量。所有海军和水运单位+2 [ICON_Movement] 移动力。[ICON_TradeRoute] 贸易路线每经过3个单元格便+1 [ICON_Gold] 金币，每经过5个单元格便+1 [ICON_CULTURE] 文化值。"		),
("en_US",	    "LOC_TRAIT_LEADER_WILLIAM_OF_ORANGE_DESCRIPTION",		"Culture Bomb adjacent tiles when completing a Harbor. +1 [ICON_TradeRoute] Trade Route capacity for each Shipyard building. +2 [ICON_Movement] Movement for all sea units and embarked units. Your [ICON_TradeRoute] Trade Routes receive +1 [ICON_Gold] gold for each 3 tiles and +1 [ICON_CULTURE] culture for each 5 tiles of distance they are passing through."	),
--GERMANY 德国 LEADER_GEDEMO_BISMARK
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GEDEMO_BISMARK_NAME",		"铁血宰相"		),
("en_US",	    "LOC_TRAIT_LEADER_GEDEMO_BISMARK_NAME",		"Eiserner Kanzler"	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GEDEMO_BISMARK_DESCRIPTION",		"战斗单位在领土中或与领土相邻的单元格上战斗时+10 [ICON_Strength] 战斗力。建造商业同业工会后奖励一个近战单位。解锁“电力”科技后，所有城市获得10点免费 [ICON_POWER] 电力。"		),
("en_US",	    "LOC_TRAIT_LEADER_GEDEMO_BISMARK_DESCRIPTION",		"All units +10 [ICON_Strength] Combat Strength when fighting in or adjacent to their home territory. Receive a free melee unit upon the completion of Hansa district. After researching 'electricity' technology, all cities receive 10 free [ICON_POWER] power."	),
("zh_Hans_CN",	"LOC_ABILITY_BISMARK_FRIENDLY_TERRITORY_COMBAT_DESCRIPTION",		"在领土中或与领土相邻的单元格上战斗时+10 [ICON_Strength] 战斗力。（铁血宰相）"		),
("en_US",	    "LOC_ABILITY_BISMARK_FRIENDLY_TERRITORY_COMBAT_DESCRIPTION",		"+10 [ICON_Strength] Combat Strength when fighting in or adjacent to their home territory. (Eiserner Kanzler)"	),
("zh_Hans_CN",	"LOC_BISMARK_FRIENDLY_TERRITORY_COMBAT",		"+10 [ICON_Strength] 战斗力（铁血宰相）"		),
("en_US",	    "LOC_BISMARK_FRIENDLY_TERRITORY_COMBAT",		"+10 [ICON_Strength] Combat Strength (Eiserner Kanzler)"	),
--INDONESIA 印度尼西亚 LEADER_CVS_GAJAH_MADA
("zh_Hans_CN",	"LOC_TRAIT_LEADER_CVS_GAJAH_MADA_UA_DESCRIPTION",		"所有单位上船下船时无移动力消耗。战斗单位位于与海岸相邻的单元格上对区域发起进攻时，+10 [ICON_Strength] 战斗力。每拥有一个沿海城市便+1 [ICON_TradeRoute] 贸易路线容量并奖励一个商人。研究完学徒科技时，解锁特色单位塞班。"		),
("en_US",	    "LOC_TRAIT_LEADER_CVS_GAJAH_MADA_UA_DESCRIPTION",		"All units pay no movement to embark or disembark. All units receive +10 [ICON_Strength] Combat Strength when attacking district defense on a tile that is adjacent to coast. +1 [ICON_TradeRoute] Trade Route capacity and gain a trader unit for each coastal city you own. Unlock unique unit Cetbang upon researching Apprenticeship."	),
("zh_Hans_CN",	"LOC_ABILITY_CVS_GAJA_MADAH_UA_DESCRIPTION",		"位于与海岸相邻的单元格上对区域发起进攻时，+10 [ICON_Strength] 战斗力。（帕拉帕誓言）"		),
("en_US",	    "LOC_ABILITY_CVS_GAJA_MADAH_UA_DESCRIPTION",		"+10 [ICON_Strength] Combat Strength when attacking district defense on a tile that is adjacent to coast. (Sumpah Palapa)"	),
("zh_Hans_CN",	"LOC_ABILITY_CVS_GAJA_MADAH_UA_PREVIEW_TEXT",		"+10 [ICON_Strength] 战斗力（帕拉帕誓言）"		),
("en_US",	    "LOC_ABILITY_CVS_GAJA_MADAH_UA_PREVIEW_TEXT",		"+10 [ICON_Strength] Combat Strength (Sumpah Palapa)"	),
--PERSIA 波斯 LEADER_CVS_DARIUS
("zh_Hans_CN",	"LOC_TRAIT_LEADER_CVS_DARIUS_UA_NAME",		"阿契美尼德的遗产"),
("en_US",	    "LOC_TRAIT_LEADER_CVS_DARIUS_UA_NAME",		"Achaemenid Legacy"),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_CVS_DARIUS_UA_DESCRIPTION",		"[COLOR_FLOAT_MILITARY]这个Mod会导致游戏崩溃，请勿与黄金时代-美丽新世界共同使用。[ENDCOLOR]"),
("en_US",	    "LOC_TRAIT_LEADER_CVS_DARIUS_UA_DESCRIPTION",		"[COLOR_FLOAT_MILITARY]This mod will cause game crash. Don't use with Goldenage-Brave New World.[ENDCOLOR]"),
-- ("zh_Hans_CN",	"LOC_TRAIT_LEADER_CVS_DARIUS_UA_DESCRIPTION",		"每修建或购买一个区域便获得+1时代得分。处于 [ICON_GLORY_GOLDEN_AGE] 黄金时代时，所有单元格+1 [ICON_Gold] 金币，所有单位+10 [ICON_Strength] 战斗力。"),
