INSERT OR REPLACE INTO LocalizedText (Language, Tag, Text)
VALUES
--CIVILIZATION_AMERICA
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ROOSEVELT_COROLLARY_DESCRIPTION",		            "政体中的每个军事槽位每回合提供+2影响力点数。每个 [ICON_Envoy] 使者+1 [ICON_Gold] 金币。拥有国家公园的城市，所有单元格+1魅力。当研究完膛线科技时，解锁莾骑兵特色单位。"		),
("en_US",	    "LOC_TRAIT_LEADER_ROOSEVELT_COROLLARY_DESCRIPTION",		            "Each active Military Policy grants +2 Influence point per turn. +1 [ICON_Gold] for each active [ICON_Envoy] Envoy. +1 Appeal to all tiles in a city with a National Park. Gain the Rough Rider unique unit when they research the Rifling technology."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_FOUNDING_FATHERS_EXPANSION2_DESCRIPTION",	"单位在首都所在的大陆时，获得+5 [ICON_Strength] 战斗力。当前政体的所有外交政策槽位均转为通配符槽位。其政体中每个通配符槽位每回合提供+1 [ICON_FAVOR] 外交支持。在首都所在大陆新建城市时获得1个额外 [ICON_CITIZEN] 人口。购买单元格的价格-25%。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_FOUNDING_FATHERS_EXPANSION2_DESCRIPTION",   "Units receive a +5 [ICON_Strength] Combat Strength on their home continent. All Diplomatic policy slots in the current government are converted to Wildcard slots. +1 [ICON_FAVOR] Diplomatic Favor per turn for every Wildcard slot in their government. New cities on home continent receive an extra [ICON_CITIZEN] citizen. -25% cost when you purchase a tile."	),
-- ("zh_Hans_CN",	"LOC_BUILDING_FILM_STUDIO_EXPANSION2_DESCRIPTION",	                            "美国特色建筑。 在电气时代及之后的时代，为所在城市+100% [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]其基础产出延伸到5个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"		),
-- ("en_US",	    "LOC_BUILDING_FILM_STUDIO_EXPANSION2_DESCRIPTION",                             "A building unique to America. +100% [ICON_Tourism] Tourism pressure from this city towards other civilizations in and after the Modern era.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 5 tiles that do not already have a bonus from a Theater Tier 3 building."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ANTIQUES_AND_PARKS_DESCRIPTION",	                "与自然奇观、山脉、礁石相邻时，魅力值为“惊艳”的单元格+1 [ICON_SCIENCE] 科技值、+1 [ICON_Gold] 金币。与自然奇观、树林、沼泽相邻时，魅力值为“惊艳”的单元格+1 [ICON_CULTURE] 文化值、+1 [ICON_Gold] 金币。拥有自然奇观或国家公园的城市的所有单元格分别+1魅力。解锁“保护地球”后，拥有自然奇观的城市获得2个免费的自然学家。"		),
("en_US",	    "LOC_TRAIT_LEADER_ANTIQUES_AND_PARKS_DESCRIPTION",                  "Breathtaking tiles adjacent to either a Natural Wonder, Mountains, or Reef receive +1 [ICON_SCIENCE] Science and +1 [ICON_Gold] Gold. Breathtaking tiles adjacent to either a Wonder, Woods, or Marsh receive +1 [ICON_CULTURE] Culture and +1 [ICON_Gold] Gold. All tiles in a city with a Natural Wonder or a National Park receive +1 Appeal, respectively. After unlocking civic 'Conservation', get 2 free Naturalists from cities with a National Wonder."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ROOSEVELT_COROLLARY_ROUGH_RIDER_DESCRIPTION",		"政体中的每个军事槽位每回合提供+2影响力点数。如与城邦之间存在 [ICON_TradeRoute] 贸易路线，派往此处的1位 [ICON_Envoy] 使者将视为2位。每个 [ICON_Envoy] 使者+1 [ICON_Gold] 金币。当研究完膛线科技时，解锁莾骑兵特色单位。"		),
("en_US",	    "LOC_TRAIT_LEADER_ROOSEVELT_COROLLARY_ROUGH_RIDER_DESCRIPTION",		"Each active Military Policy grants +2 Influence point per turn. +1 [ICON_Gold] for each active [ICON_Envoy] Envoy. Envoys sent to City-States you have a [ICON_TradeRoute] Trade Route to count as two [ICON_Envoy] Envoys. Gain the Rough Rider unique unit when they research the Rifling technology."	),
("zh_Hans_CN",	"LOC_UNIT_AMERICAN_ROUGH_RIDER_DESCRIPTION",	                 	"泰迪·罗斯福作为领袖时的工业时代美国特色重骑兵单位，克制远程单位。视野为2。[NEWLINE][NEWLINE]在 [ICON_Capital] 首都所处大陆击杀敌人后可获得 [ICON_Culture] 文化值。在丘陵作战时+10 [ICON_Strength] 战斗力。维护费较低。"),
("en_US",       "LOC_UNIT_AMERICAN_ROUGH_RIDER_DESCRIPTION",                        "American unique Industrial era Heavy Cavalry unit when Teddy Roosevelt is their leader, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE][ICON_Culture] Culture is earned from kills on their [ICON_Capital] Capital's continent. +10 [ICON_Strength] Combat Strength when fighting on Hills. Lower maintenance cost."),
--CIVILIZATION_ARABIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_LAST_PROPHET_DESCRIPTION",		"当倒数第二个 [ICON_GreatProphet] 预言家被招募后（如果您尚未获得 [ICON_GreatProphet] 大预言家），自动获得最后一个大预言家。沙漠地形单元格为圣地区提供标准 [ICON_Faith] 信仰值相邻加成。每个寺庙建筑+1 [ICON_TradeRoute] 贸易路线容量，来自 [ICON_TradeRoute] 贸易路线的宗教压力+100%。没有宗教信仰的城市-6忠诚度。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_LAST_PROPHET_DESCRIPTION",		"Automatically receive the final [ICON_GreatProphet] Great Prophet when the next-to-last one is claimed (if you have not earned a [ICON_GreatProphet] Great Prophet already). Desert terrains provide standard [ICON_FAITH] faith adjacency bonus for Holy Site. +1 [ICON_TradeRoute] TradeRoute Capacity from each Temple Building. +100% Religious pressure from your [ICON_TradeRoute] Trade Routes. -6 loyalty for cities without religious belief."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_RIGHTEOUSNESS_OF_FAITH_DESCRIPTION",	"信仰你的宗教的城市可以使用 [ICON_Faith] 信仰值消耗的1/10来购买宗教祭祀建筑。祭祀建筑为阿拉伯城市的 [ICON_Science] 科技值、 [ICON_Faith] 信仰值、 [ICON_Culture] 文化值增加10%产量。每座信奉你的宗教的城市+2 [ICON_Science] 科技值、 +2 [ICON_FAITH] 信仰值，若为外国城市则翻倍。战斗单位与信仰其他宗教的文明进行战争时+10 [ICON_Strength] 战斗力。"		),
("en_US",	    "LOC_TRAIT_LEADER_RIGHTEOUSNESS_OF_FAITH_DESCRIPTION",  "The worship building for your Religion can be purchased by any player for just 1/10 of the [ICON_Faith] Faith cost.  This worship building is enhanced to add 10% to the [ICON_Science] Science, [ICON_Faith] Faith, and [ICON_Culture] Culture output of Arabian cities. +2 [ICON_Science] Science and +2 [ICON_FAITH] Faith for each city following your Religion, double if it is a foreign city. Combat units gain +10 [ICON_Strength] Combat Strength when flighting with enemies of other religion."	),
-- ("zh_Hans_CN",	"LOC_BUILDING_MADRASA_DESCRIPTION",	                    "阿拉伯特色建筑。提供与学院区的相邻加成等量的 [ICON_Faith] 信仰值。[NEWLINE][ICON_SCIENCE] 科技值加成延伸到4个单元格中的每个市中心(此加成每座城市仅能应用1次)。[NEWLINE]由于教育水平的提高，城市中每个 [ICON_Citizen] 公民带来0.1 [ICON_SCIENCE] 科技值。"		),
-- ("en_US",	    "LOC_BUILDING_MADRASA_DESCRIPTION",                     "A building unique to Arabia. Bonus [ICON_Faith] Faith equal to the adjacency bonus of the Campus district. [NEWLINE]Bonus [ICON_SCIENCE] Science extends to each city center within 4 tiles. This bonus applies once to a city. [NEWLINE]+0.1 [ICON_SCIENCE] Science per [ICON_Citizen] Citizen in the city due to better education."	),
("zh_Hans_CN",	"GOLDEN_LOC_RIGHTEOUSNESS_OF_FAITH",		            "+10 [ICON_Strength] （正义的信仰）"		),
("en_US",	    "GOLDEN_LOC_RIGHTEOUSNESS_OF_FAITH",		            "+10 [ICON_Strength] (Righteousness of the Faith)"	),
("zh_Hans_CN",  "LOC_UNIT_ARABIAN_MAMLUK_DESCRIPTION",                   "阿拉伯特色的中世纪重骑兵单位，替代骑士，克制远程单位。视野为2。[NEWLINE][NEWLINE]在每回合结束时（甚至是在移动或攻击后），自动恢复生命值。"),
("en_US",       "LOC_UNIT_ARABIAN_MAMLUK_DESCRIPTION",                  "Arabian unique Medieval era Heavy Cavalry unit that replaces the Knight, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE]Heals at the end of every turn, even after moving or attacking."),
--CIVILIZATION_AUSTRALIA
("zh_Hans_CN",	"LOC_TRAIT_LEADER_CITADEL_CIVILIZATION_EXPANSION2_DESCRIPTION",		"若其在过去10回合内遭到宣战，则+100% [ICON_PRODUCTION] 生产力，单位+5 [ICON_Strength] 战斗力，+2 [ICON_Movement] 移动力。针对同一个敌人进行作战时，如果他们与其盟友位于5个单元格以内，则共享掠夺奖励和获得的战斗经验值。其同盟针对同一个敌人作战可获得同盟点数。通往盟友城市的贸易路线为双方+1 [ICON_Science] 科技值+1 [ICON_Culture] 文化值。"		),
("en_US",	    "LOC_TRAIT_LEADER_CITADEL_CIVILIZATION_EXPANSION2_DESCRIPTION",		"+100% [ICON_PRODUCTION] Production, +5 [ICON_Strength] combat strength, +2 [ICON_Movement] movement for units if they have received a declaration of war in the past 10 turns. When at war with a common foe, they and their allies share pillage rewards and share combat experience gains if within 5 tiles. Their Alliances gain Alliance Points for being at war with a common foe. Your [ICON_TradeRoute] Trade Routes to an Ally's city provide +1 [ICON_Science] Science and +1 [ICON_Culture] Culture for both cities."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_LAND_DOWN_UNDER_DESCRIPTION",		        "拥有迷人魅力的单元格中的专业化区域的相应产出+2；惊艳的单元格则产出+4（但区域间的少量相邻加成和市中心的标准相邻加成无效）。收获资源与移除地貌时的产出翻倍。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_LAND_DOWN_UNDER_DESCRIPTION",		        "Yields from Specialty districts are +2 in tiles with Charming Appeal, +4 in Breathtaking (however, minor district adjacency bonus and city center standard adjacency bonus are disabled). Double the yields when you harvest resource or remove feature."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_OUTBACK_STATION_DESCRIPTION",		        "解锁建造者修建澳大利亚特色改良设施内陆牧场的能力。[NEWLINE][NEWLINE]+1 [ICON_FOOD] 食物、+1 [ICON_Production] 生产力、+1 [ICON_Housing] 住房。每个相邻牧场+1 [ICON_Food] 食物。解锁“现代航空”后，每个相邻牧场+1 [ICON_Production] 生产力。解锁“蒸汽动力”和“紧急部署”后，每个相邻内陆牧场将获得更多 [ICON_PRODUCTION] 生产力和 [ICON_FOOD] 食物。[NEWLINE][NEWLINE]只能建在沙漠、草原和平原及其对应的丘陵单元格上。"		),
("en_US",	    "LOC_IMPROVEMENT_OUTBACK_STATION_DESCRIPTION",		        "Unlocks the Builder ability to construct an Outback Station, unique to Australia.[NEWLINE][NEWLINE]+1 [ICON_FOOD] Food, +1 [ICON_Production] Production, and +1 [ICON_Housing] Housing. +1 [ICON_Food] Food for each adjacent Pasture. Additional [ICON_FOOD] Food and [ICON_PRODUCTION] Production as you advance through the Technology and Civic Tree for adjacent Outback Stations and Pastures. Can only be built in Desert, Grassland, Plains and their corresponding hills tiles."	),
("zh_Hans_CN",  "LOC_UNIT_DIGGER_DESCRIPTION",           "澳大利亚特色的电气时代近战单位，替代步兵，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]在海岸单元格上作战时+10 [ICON_Strength] 战斗力。在中立或外国领土上作战时+5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_DIGGER_DESCRIPTION",           "Australian unique Modern era Melee unit that replaces Infantry, counters Anti-Cavalry units well. Sight: 1 [NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength when fighting on Coastal tiles. +5 [ICON_Strength] Combat Strength when fighting on Neutral or Foreign Territory."),
--CIVILIZATION_AZTEC
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_LEGEND_FIVE_SUNS_DESCRIPTION",		"可使用建造者劳动力来推进20%的区域建设进程。与湖泊和市中心同时相邻的单元格+1所有产量。击杀单位可获得等同于被击杀单位 [ICON_Strength] 战斗力50%的 [ICON_Science] 科技值。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_LEGEND_FIVE_SUNS_DESCRIPTION",		"Spend Builder charges to complete 20% of the original district cost. +1 all types of yields for tiles that are adjacent to both lake and city center. Combat victories provide [ICON_Science] Science equal to 50% of the [ICON_Strength] Combat Strength of the defeated unit."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GIFTS_FOR_TLATOANI_DESCRIPTION",		    "您的国境内已开发的每种加成资源提供1点 [ICON_Amenities] 宜居度，并像奢侈资源一样分配给您的城市。领土中每种改良的奢侈品资源将为军事单位+1 [ICON_Strength] 战斗力。你的近战、抗骑兵和骑兵单位击败其他文明的军事单位时有几率将其转化为建造者并俘获。无法训练或购买建造者单位。"		),
("en_US",	    "LOC_TRAIT_LEADER_GIFTS_FOR_TLATOANI_DESCRIPTION",		    "Your Bonus resources behave like Luxury resources, providing 1 [ICON_Amenities] Amenity per type. Military units receive +1 [ICON_Strength] Combat Strength when attacking for each different Luxury resource improved in Aztec lands. Your melee, anti-cavalry, and cavalry units can transfrom enemy units to workers and capture them. Can not train or purchase builder."	),
("zh_Hans_CN",	"LOC_BUILDING_TLACHTLI_XP1_DESCRIPTION",		    "阿兹特克特色建筑。+2 [ICON_Amenities] 宜居度 （无 [ICON_POWER] 电力加成）。+3 [ICON_TOURISM] 旅游业绩。+3 [ICON_GreatGeneral] 大将军点数。+2 [ICON_Culture] 文化值、+2 [ICON_Faith] 信仰值。解锁特殊项目“献祭活人”。[NEWLINE][NEWLINE]阿兹特克特色项目，以牺牲1 [ICON_Citizen] 人口为代价，进行时此城市每回合可额外产出当前 [ICON_Production] 生产力水平15%的 [ICON_Science] 科技值、 [ICON_Culture] 文化值和 [ICON_Faith] 信仰值，完成时获得2个建造者。"		),
("en_US",	    "LOC_BUILDING_TLACHTLI_XP1_DESCRIPTION",		    "A building unique to the Aztecs. Provides +2 [ICON_Amenities] Amenity (no [ICON_POWER] power bonus), +3 [ICON_TOURISM] Tourism, and +3 [ICON_GreatGeneral] GreatGeneral points. +2 [ICON_Culture] Culture and +2 [ICON_Faith] Faith. Unlock special project 'Sacrifice of the living'.[NEWLINE][NEWLINE]Aztec unique project, gain [ICON_Science] Science, [ICON_Culture] Culture, and [ICON_Faith] Faith equal to 15% of [ICON_Production] Production in this city with the price of sacrificing one [ICON_Citizen] Citizen. Gain 2 builders when completing this project."	),
("zh_Hans_CN",	"LOC_PROJECT_SACRIFICE_NAME",		            "献祭活人"		),
("en_US",	    "LOC_PROJECT_SACRIFICE_NAME",		            "Sacrifice of the living"	),
("zh_Hans_CN",	"LOC_PROJECT_SACRIFICE_DESCRIPTION",		    "阿兹特克特色项目，以牺牲1 [ICON_Citizen] 人口为代价，进行时此城市每回合可额外产出当前 [ICON_Production] 生产力水平10%的 [ICON_Science] 科技值、 [ICON_Culture] 文化值和 [ICON_Faith] 信仰值，完成时获得2个建造者。"		),
("en_US",	    "LOC_PROJECT_SACRIFICE_DESCRIPTION",		    "Aztec unique project, gain [ICON_Science] Science, [ICON_Culture] Culture, and [ICON_Faith] Faith equal to 10% of [ICON_Production] Production in this city with the price of sacrificing one [ICON_Citizen] Citizen. Gain 2 builders when completing this project."	),
("zh_Hans_CN",	"LOC_UNIT_AZTEC_EAGLE_WARRIOR_DESCRIPTION",		        "阿兹特克远古时代特色近战单位，取代勇士，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE] [ICON_Movement] 移动力比勇士高。"		),
("en_US",	    "LOC_UNIT_AZTEC_EAGLE_WARRIOR_DESCRIPTION",		        "Aztec unique Ancient era Melee unit that replaces the Warrior, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE] Has more [ICON_Movement] movement than Warrior."	),
--CIVILIZATION_BABYLON
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_BABYLON_DESCRIPTION",		            "[ICON_TechBoosted] 尤里卡提供科技79%的 [ICON_SCIENCE] 科技值。+100% [ICON_GreatScientist] 大科学家点数。学院建筑 [ICON_SCIENCE] 科技值基础产出-75%。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_BABYLON_DESCRIPTION",		            "[ICON_TechBoosted] Eurekas provide 79% of the [ICON_SCIENCE] Science for technologies. +100% [ICON_GreatScientist] Great Scientist points. -75% [ICON_SCIENCE] Science base yield for Campus buildings."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_HAMMURABI_XP1_DESCRIPTION",		            "游戏开始时便获得“法典”市政。首次建造出一种专业化区域（市政广场除外）时，获得当前可在该区域中建造的、所需 [ICON_PRODUCTION] 生产力最低的建筑。首次建造任意其他区域时，获得1名 [ICON_ENVOY] 使者。"		),
("en_US",	    "LOC_TRAIT_LEADER_HAMMURABI_XP1_DESCRIPTION",		            "Start the game with civic 'Code of Laws' unlocked. When each specialty district type except the Government Plaza is constructed for the first time receive the lowest [ICON_PRODUCTION] Production cost building that can currently be constructed in that district. Receive an [ICON_ENVOY] Envoy when any other district is constructed for the first time."	),
("zh_Hans_CN",	"LOC_BUILDING_PALGUM_DESCRIPTION",		            "巴比伦特色建筑。+2 [ICON_HOUSING] 住房、+1 [ICON_FOOD] 食物、+1 [ICON_PRODUCTION] 生产力。淡水单元格产出的 [ICON_FOOD] 食物+1。城市必须建在河流旁。"		),
("en_US",	    "LOC_BUILDING_PALGUM_DESCRIPTION",		            "A building unique to Babylon. +2 [ICON_HOUSING] Housing, +1 [ICON_FOOD] Food, and +1 [ICON_PRODUCTION] Production. Freshwater tiles receive +1 [ICON_FOOD] Food. City must be adjacent to a River."	),
("zh_Hans_CN",  "LOC_UNIT_BABYLONIAN_SABUM_KIBITTUM_DESCRIPTION",           "巴比伦特色远古时代近战单位，替代勇士，克制抗骑兵单位。视野为3。[NEWLINE][NEWLINE]与轻重骑兵类单位作战时+17 [ICON_Strength] 战斗力。此单位拥有3点视野。"),
("en_US",       "LOC_UNIT_BABYLONIAN_SABUM_KIBITTUM_DESCRIPTION",           "Babylonian unique Ancient era melee unit that replaces the Warrior, counters Anti-Cavalry units well. Sight: 3 [NEWLINE][NEWLINE]+17 [ICON_Strength] Combat Strength against Heavy and Light Cavalry promotion class units. This unit has 3 sight."),
--CIVILIZATION_BRAZIL
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_AMAZON_DESCRIPTION",		"雨林单元格为所有专业化区域提供标准相邻加成（但区域间的少量相邻加成和市中心的标准相邻加成无效）。未改良的雨林单元格+1 [ICON_Food] 食物，解锁“保护地球”后+2 [ICON_Culture] 文化值。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_AMAZON_DESCRIPTION",		"Rainforest tiles provide +1 adjacency bonus for Specialty districts (however, minor district adjacency bonus and city center standard adjacency bonus are disabled). Non-improved rainforest tiles provide +1 [ICON_Food] Food, and +2 [ICON_Culture] Culture when Conservation unlocks. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MAGNANIMOUS_DESCRIPTION",		    "赞助一位 [ICON_GreatPerson] 伟人时消耗的 [ICON_Gold] 金钱减少25%。招募或赞助一位 [ICON_GreatPerson] 伟人后，将返还所消耗 [ICON_GreatPerson] 伟人点数的25%。"		),
("en_US",	    "LOC_TRAIT_LEADER_MAGNANIMOUS_DESCRIPTION",		    "25% [ICON_Gold] Gold discount when patronizing a [ICON_GreatPerson] Great Person. After recruiting or patronizing a [ICON_GreatPerson] Great Person, 25% of its [ICON_GreatPerson] Great Person point cost is refunded. "	),
("zh_Hans_CN",	"LOC_DISTRICT_STREET_CARNIVAL_EXPANSION2_DESCRIPTION",		    "巴西特色区域，代替娱乐设施区域。[NEWLINE][NEWLINE]+1 [ICON_Amenities] 宜居度。区域中的专家产出更高。解锁“狂欢节”项目。项目开始时即提供+1 [ICON_Amenities] 宜居度，项目完成后可获得若干 [ICON_GreatPerson] 伟人点数。无法建在拥有“科帕卡瓦纳”的城市中。[NEWLINE][NEWLINE]与街头狂欢节相邻时+1 [ICON_Amenities] 宜居度。（需“城市圈”游戏模式）[NEWLINE]与科帕卡瓦纳相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与市中心相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与市政广场相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与国家娱乐中心相邻时+2 [ICON_Amenities] 宜居度。[NEWLINE][NEWLINE]为相邻单元格提供+2魅力。"		),
("en_US",	    "LOC_DISTRICT_STREET_CARNIVAL_EXPANSION2_DESCRIPTION",		    "A district unique to Brazil. Replaces the Entertainment Complex district. [NEWLINE][NEWLINE]Provide +1 [ICON_Amenities] Amenities. Specialists on this district provide extra yields. Also unlocks the Carnival project, which grants an additional +1 [ICON_Amenities] Amenity when underway and a variety of [ICON_GreatPerson] Great People points once completed. Cannot be built in a city with a Copacabana. [NEWLINE][NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Street Carnival.(Requries Megalopolis game mode)[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to City center.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Government plaza.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Water Street Carnival.[NEWLINE]+2 [ICON_Amenities] Amenity when adjacent to National Entertainment Complex.[NEWLINE][NEWLINE]+2 Appeal."	),
("zh_Hans_CN",	"LOC_DISTRICT_WATER_STREET_CARNIVAL_EXPANSION2_DESCRIPTION",    "巴西特色区域，代替水上乐园。[NEWLINE][NEWLINE]+1 [ICON_Amenities] 宜居度。区域中的专家产出更高。解锁“狂欢节”项目，项目启动后即可额外+1 [ICON_Amenities] 宜居度，项目完成后可获得若干 [ICON_GreatPerson] 伟人点数。无法建在拥有“街头狂欢节”的城市中。无法建在礁石上。[NEWLINE][NEWLINE]与街头狂欢节相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与科帕卡瓦纳相邻时+1 [ICON_Amenities] 宜居度。（需“城市圈”游戏模式）[NEWLINE]与市中心相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与市政广场相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与国家娱乐中心相邻时+2 [ICON_Amenities] 宜居度。[NEWLINE][NEWLINE]为相邻单元格提供+2魅力。"		),
("en_US",	    "LOC_DISTRICT_WATER_STREET_CARNIVAL_EXPANSION2_DESCRIPTION",	"A district unique to Brazil. Replaces the Water Park district.[NEWLINE][NEWLINE]Provide +1 [ICON_Amenities] Amenities. Specialists on this district provide extra yields. Also unlocks the Carnival project, which grants an additional +1 [ICON_Amenities] Amenity when underway and a variety of [ICON_GreatPerson] Great People points once completed. Cannot be built in a city with a Street Carnival. Cannot be built on Reef.[NEWLINE][NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Street Carnival.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to City center.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Government plaza.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Water Street Carnival.(Requries Megalopolis game mode)[NEWLINE]+2 [ICON_Amenities] Amenity when adjacent to National Entertainment Complex.[NEWLINE][NEWLINE]+2 Appeal."	),
--CIVILIZATION_BYZANTIUM
("zh_Hans_CN",	"LOC_TRAIT_LEADER_BASIL_NAME",		                    "紫罗兰贵族"		),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_BYZANTIUM_DESCRIPTION",		    "每有1座圣城皈依拜占庭的宗教（也包括拜占庭的圣城），单位便获得+3 [ICON_Strength] 战斗力或 [ICON_Religion] 宗教战斗力。击杀敌方文明或城邦单位时，拜占庭的宗教将传播到附近的城市。在有宗教信仰的城市中建造寺庙建筑后便奖励1个使徒单位。你的使徒单位可以选择任何可用的升级。拥有圣地区的城市+2 [ICON_GreatProphet] 大预言家点数。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_BYZANTIUM_DESCRIPTION",		    "Units receive +3 [ICON_Strength] Combat Strength or [ICON_Religion] Religious Strength for each Holy City converted to Byzantium's Religion (including Byzantium's Holy City). Byzantium's Religion is spread to nearby cities when an enemy civilization or city-state unit is defeated. Gain a free Apostle in cities with a Tample building and with a religion. Your Apostle can choose from any possible promotion. +2 [ICON_GreatProphet] Great Prophet point from cities with a Holy Site district."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_BASIL_DESCRIPTION",		            "对所信仰宗教与拜占庭相同的城市发起进攻时，轻重骑兵将输出全额伤害。解锁“城堡”科技后，所有城市获得远古城墙和中世纪城墙。解锁“王权神授”市政后将获得特色单位“靖抚骑兵”。"		),
("en_US",	    "LOC_TRAIT_LEADER_BASIL_DESCRIPTION",		            "Heavy and Light Cavalry units do full damage against cities following the same religion as Byzantium. Gain free walls and castle in all cities when the Castle tech is discovered. Gain the Tagma unique unit when the Divine Right civic is discovered."	),
("zh_Hans_CN",	"LOC_DISTRICT_HIPPODROME_EXPANSION2_DESCRIPTION",		"拜占庭特色区域。替代“娱乐设施”区域。[NEWLINE][NEWLINE]+1 [ICON_Amenities] 宜居度。建成跑马场及此区域中的建筑后，将获得1个重骑兵单位（此单位创建时不需要资源，维护也不需要资源）。[NEWLINE][NEWLINE]与跑马场相邻时+1 [ICON_Amenities] 宜居度。（需“城市圈”游戏模式）[NEWLINE]与市中心相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与市政广场相邻时+1 [ICON_Amenities] 宜居度。[NEWLINE]与国家娱乐中心相邻时+2 [ICON_Amenities] 宜居度。[NEWLINE][NEWLINE]为相邻单元格提供+1魅力。"		),
("en_US",	    "LOC_DISTRICT_HIPPODROME_EXPANSION2_DESCRIPTION",		 "A district unique to Byzantium. Replaces the Entertainment Complex district.[NEWLINE][NEWLINE]+1 [ICON_Amenities] Amenities. When the Hippodrome and buildings in this district are constructed receive a Heavy Cavalry unit. The free unit does not require resources when created or to maintain. [NEWLINE][NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Hippodrome.(Requries Megalopolis game mode)[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to City center.[NEWLINE]+1 [ICON_Amenities] Amenity when adjacent to Government plaza.[NEWLINE]+2 [ICON_Amenities] Amenity when adjacent to National Entertainment Complex.[NEWLINE][NEWLINE]+1 Appeal."	),
("zh_Hans_CN",  "LOC_UNIT_BYZANTINE_TAGMA_DESCRIPTION",           "巴兹尔二世的特色中世纪单位，替代骑士，克制远程单位。视野为2。[NEWLINE][NEWLINE]甲胄骑兵1单元格范围内的陆地单位+4 [ICON_Strength] 战斗力或 [ICON_RELIGION] 宗教战斗力。"),
("en_US",       "LOC_UNIT_BYZANTINE_TAGMA_DESCRIPTION",           "Basil II's unique Medieval era unit that replaces the Knight, counters ranged units well. Sight: 2 [NEWLINE][NEWLINE]Land units within 1 tile of the Tagma receive +4 [ICON_Strength] Combat Strength or [ICON_RELIGION] Religious Strength."),
--CIVILIZATION_CANADA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_FACES_OF_PEACE_DESCRIPTION",		"无法对城邦宣战或发动突袭战争。无法对加拿大发动突袭战争。每回合每获得50点 [ICON_TOURISM] 旅游业绩，即可每回合获得1点 [ICON_Favor] 外交支持。成功完成紧急状况或积分竞赛后+100% [ICON_Favor] 外交支持。每回合获得5点影响力点数，解锁“外交部门”市政后翻倍。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_FACES_OF_PEACE_DESCRIPTION",		"Cannot declare war on City-States or surprise wars. Surprise wars cannot be declared on Canada. For every 50 [ICON_TOURISM] Tourism per turn earn 1 [ICON_Favor] Diplomatic Favor per turn. Receive +100% [ICON_Favor] Diplomatic Favor from successfully completing an Emergency or Scored Competition. Gain 5 influence points per turn, double after unlocking civic 'Diplomatic Service'."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_LAST_BEST_WEST_DESCRIPTION",		        "无需前置市政便可在冻土上建造农场。在冻土和冻土丘陵上的所有矿山和伐木场提供的 [ICON_PRODUCTION] 生产力+2、农场和营地提供的 [ICON_FOOD] 食物+2。冻土（丘陵）、雪地（丘陵）地形上的单元格购买费用降低50%。冻土地形为你的专业化区域提供少量相邻加成，雪地地形为你的专业化区域提供标准相邻加成（但区域间的少量相邻加成和市中心的标准相邻加成无效）。"		),
("en_US",	    "LOC_TRAIT_LEADER_LAST_BEST_WEST_DESCRIPTION",		        "Allows Farms to be built on Tundra terrain without researching required civic. In Tundra and Tundra Hills, all Mines and Lumber Mills provide +2 [ICON_PRODUCTION] Production, all Farms and Camps provide +2 [ICON_FOOD] Food. Reduces the purchase cost of tiles in Tundra (hills) and Snow (hills) terrain types by 50%. Specialty districts gain a Small adjacency bonus from Tundra terrain and a Standard adjacency bonus from Snow terrain (however, minor district adjacency bonus and city center standard adjacency bonus are removed)."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_ICE_HOCKEY_RINK_DESCRIPTION",		        "解锁建造者建造加拿大特色改良设施冰球场的能力。[NEWLINE][NEWLINE]+2 [ICON_AMENITIES] 宜居度、+2魅力；每个相邻的冻土和冻土丘陵单元格提供+1 [ICON_CULTURE] 文化值，每个相邻的雪地和雪地丘陵单元格提供+2 [ICON_CULTURE] 文化值。每个相邻的娱乐中心提供+4 [ICON_Gold] 金币。[NEWLINE]解锁“职业体育”市政后，+2 [ICON_FOOD] 食物和 [ICON_PRODUCTION] 生产力。若与建筑“体育场”相邻，+4 [ICON_CULTURE] 文化值。解锁“飞行”科技后，[ICON_CULTURE] 文化值可提供 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]可以修建在冻土、冻土丘陵、雪地和雪地丘陵之上。每个城市只能修建1座。"		),
("en_US",	    "LOC_IMPROVEMENT_ICE_HOCKEY_RINK_DESCRIPTION",		        "Unlocks the Builder ability to construct an Ice Hockey Rink, unique to Canada.[NEWLINE][NEWLINE]+2 [ICON_AMENITIES] Amenity and +2 Appeal. +1 [ICON_CULTURE] Culture for each adjacent Tundra and Tundra Hills tile. +2 [ICON_CULTURE] Culture for each adjacent Snow and Snow Hills tile. +4 [ICON_Gold] Gold for each adjacent Entertainment Complex.[NEWLINE]+2 [ICON_FOOD] Food and [ICON_PRODUCTION] Production once the Professional Sports civic is unlocked. +4 [ICON_CULTURE] Culture if adjacent to a Stadium building. Provides [ICON_Tourism] Tourism from [ICON_CULTURE] Culture once Flight is unlocked.  [NEWLINE][NEWLINE]Can be built on Tundra, Tundra Hills, Snow, and Snow Hills. One per city. "	),
("zh_Hans_CN",  "LOC_UNIT_CANADA_MOUNTIE_DESCRIPTION",           "加拿大特色的电气时代轻骑兵单位，克制攻城单位。视野为2。[NEWLINE][NEWLINE]能创建国家公园。在任意国家公园附近的2个单元格内战斗时+5 [ICON_Strength] 战斗力。如果为己方国家公园，则在加成基础上额外再+5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_CANADA_MOUNTIE_DESCRIPTION",           "Canadian unique Modern era Light Cavalry unit, counters Siege units well. Sight: 2 [NEWLINE][NEWLINE] Can create a National Park. +5 [ICON_Strength] Combat Strength when fighting within 2 tiles of a National Park. Additional +5 [ICON_Strength] Combat Strength when fighting within 2 tiles of a National Park you own."),
--CIVILIZATION_CHINA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_DYNASTIC_CYCLE_EXPANSION2_DESCRIPTION",		"在每个游戏时代中获得1个随机 [ICON_TechBoosted] 尤里卡和随机 [ICON_CivicBoosted] 鼓舞。[ICON_TechBoosted] 尤里卡和 [ICON_CivicBoosted] 鼓舞额外提升10%的科技和市政。处于 [ICON_GLORY_GOLDEN_AGE] 黄金时代时，所有单元格+1魅力；但处于 [ICON_GLORY_DARK_AGE] 黑暗时代时，所有城市-1 [ICON_AMENITIES] 宜居度。远程单位在回合中若没有移动则+1额外攻击次数。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_DYNASTIC_CYCLE_EXPANSION2_DESCRIPTION",		"Gain one random [ICON_TechBoosted] Eureka and random [ICON_CivicBoosted] Inspiration in each game era. [ICON_TechBoosted] Eurekas and [ICON_CivicBoosted] Inspirations provide an extra 10% of civics and technologies. If you are in a [ICON_GLORY_GOLDEN_AGE] golden age, +1 Appeal for all tiles in you territory. But if you are in a [ICON_GLORY_DARK_AGE] dark age, -1 [ICON_AMENITIES] Amenity for all cities. +1 addtional attack per turn for Range unit if it has not moved."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_FIRST_EMPEROR_EXPANSION2_DESCRIPTION",		    "建造者会获得1个额外使用次数。建造者可以消耗使用次数，立刻推进远古时代，古典时代，中世纪和文艺复兴时期奇观20%的建设进度。建成世界奇观后奖励一个奇观所处时代的随机 [ICON_TechBoosted] 尤里卡和 [ICON_CivicBoosted] 鼓舞。"		),
("en_US",	    "LOC_TRAIT_LEADER_FIRST_EMPEROR_EXPANSION2_DESCRIPTION",		    "When building Wonders Ancient, Classical, Medieval, and Renaissance Era, you may spend Builder charges to complete 20% of the original wonder cost. Builders receive an additional charge. Once a world wonder is finished, recieve a random [ICON_TechBoosted] Eurekas and [ICON_CivicBoosted] Inspirations from the era of the wonder. "	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_GREAT_WALL_EXPANSION2_DESCRIPTION",		    "解锁建造者建造长城的能力。[NEWLINE][NEWLINE]如果和其他长城相邻，将获得 [ICON_Gold] 金币加成。随着科技和文化进步，与长城相邻将获得额外的 [ICON_Culture] 文化值和 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]非山脉地块的长城具有堡垒的功能（该单元格的 [ICON_Movement] 移动力消耗增加1点。进驻单位增加 [ICON_Strength] 防御力，并自动获得两回合驻扎加成。）也可修建在山脉上。[NEWLINE][NEWLINE]只能修建在相邻的单元格上。只能建成线状。无法被人为掠夺和破坏。"		),
("en_US",	    "LOC_IMPROVEMENT_GREAT_WALL_EXPANSION2_DESCRIPTION",		    "Unlocks the Builder ability to construct the Great Wall, unique to China.[NEWLINE][NEWLINE]Bonus to [ICON_Gold] Gold if adjacent to other segments. Additional [ICON_Culture] Culture and [ICON_Tourism] Tourism as you advance through the Technology and Civic Tree for adjacent segments.[NEWLINE][NEWLINE] +1 [ICON_Movement] Movement cost for this tile. Occupying unit receives [ICON_Strength] Defense Strength, and automatically gains 2 turns of fortification. Can be built on mountain tiles. [NEWLINE][NEWLINE] Can only be built at adjacent tiles that without cover. Must build in line.Can only be pillaged (never destroyed) by natural disasters."	),
("zh_Hans_CN",	"LOC_ABILITY_RANGE_EXTRA_ATTACK_DESCRIPTION",		    "在回合中若没有移动则+1额外攻击次数。"		),
("en_US",	    "LOC_ABILITY_RANGE_EXTRA_ATTACK_DESCRIPTION",		    "+1 addtional attack per turn if it has not moved."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_FIRST_EMPEROR_NAME",		    "第一帝国"		),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_KUBLAI_DESCRIPTION",		    "首次在另一个文明的城市中建立 [ICON_TradingPost] 贸易站时或占领的城市不少于10 [ICON_CITIZEN] 人口时，随机获得一项 [ICON_TechBoosted] 尤里卡和 [ICON_CivicBoosted] 鼓舞。占领的城市不少于20 [ICON_CITIZEN] 人口时，奖励+1 [ICON_Governor] 总督头衔和+1 [ICON_TradeRoute] 贸易路线容量；占领的城市不少于30 [ICON_CITIZEN] 人口时，其所有非食物产出+20%。"		),
("en_US",	    "LOC_TRAIT_LEADER_KUBLAI_DESCRIPTION",		    "Receive a random [ICON_TechBoosted] Eureka and [ICON_CivicBoosted] Inspiration when establishing a [ICON_TradingPost] Trading Post in another Civilization's city for the first time or when captured cities have no less than 10 [ICON_CITIZEN] citizens. Receive +1 [ICON_Governor] Governor title and +1 [ICON_TradeRoute] trade route capacity when captured cities have no less than 20 [ICON_CITIZEN] citizens; Receive +20% for all non-food yields for captured cities when they have no less than 30 [ICON_CITIZEN] citizens."	),
("zh_Hans_CN",	"LOC_ABILITY_KUBLAI_POLICY_SLOT_COMBAT_BONUS_DESCRIPTION",		    "当前政体中每有一个经济政策，则+1 [ICON_Strength] 战斗力。（忽必烈）"		),
("en_US",	    "LOC_ABILITY_KUBLAI_POLICY_SLOT_COMBAT_BONUS_DESCRIPTION",		    "+1 [ICON_Strength] Combat Strength for each Economic policy slotted in the current government. (Kublai)"	),
("zh_Hans_CN",	"LOC_UNIT_CHINESE_CROUCHING_TIGER_DESCRIPTION",		    "中国特色的中世纪远程单位，克制近战单位。 [ICON_Range] 射程为2。视野为1。"		),
("en_US",	    "LOC_UNIT_CHINESE_CROUCHING_TIGER_DESCRIPTION",		    "Chinese unique Medieval era Ranged unit, counters Melee units well.Ranged unit with a [ICON_Range] Range of 2 and high combat strength. Sight: 1 "	),
--CIVILIZATION_CREE
("zh_Hans_CN",	"LOC_LEADER_POUNDMAKER_ABILITY_DESCRIPTION",		                    "所有同盟类型均可形成共享视野。所有盟友的同盟点数每回合额外增加0.25。你的陆地战斗单位在森林和雨林中移动无 [ICON_Movement] 移动力惩罚且视野可以穿过森林和雨林。"		),
("en_US",	    "LOC_LEADER_POUNDMAKER_ABILITY_DESCRIPTION",		                    "All Alliance types provide Shared Visibility. Alliance Points with all allies increase by an additional .25 per turn. Your land combat units have faster [ICON_Movement] Movement on Forest and Jungle,  and can see through Forest and Jungle. "	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_MEKEWAP_DESCRIPTION",		                            "解锁建造者建造棚屋（克里特色改良设施）的能力。[NEWLINE][NEWLINE]+1.5 [ICON_Housing] 住房。相邻的每个奢侈品资源可使其+1 [ICON_GOLD] 金币；相邻的每个加成资源可使其+1 [ICON_FOOD] 食物；相邻的每个战略资源可使其+1 [ICON_PRODUCTION] 生产力。研究“马镫”和“卫生设备”后这些奖励翻倍。[NEWLINE][NEWLINE]必须建在与加成或奢侈资源相邻的单元格上。无法建造另一个棚屋旁。"		),
("en_US",	    "LOC_IMPROVEMENT_MEKEWAP_DESCRIPTION",		                            "Unlocks the Builder ability to construct a Mekewap, unique to Cree.[NEWLINE][NEWLINE]+1.5 [ICON_Housing] Housing. For every adjacent Bonus Resources +1 [ICON_GOLD] Gold. For every adjacent Bonus Resources +1 [ICON_FOOD] Food. For every adjacent Strategic Resources +1 [ICON_PRODUCTION] Production. Double these bonus after unlocking Tech 'Stirrups' and 'Sanitation'.[NEWLINE][NEWLINE]Must be placed adjacent to a Bonus or Luxury resource. Cannot be built adjacent to another Mekewap. "	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_CREE_TRADE_GAIN_TILES_DESCRIPTION",		        "森林单元格为你的所有专业化区域提供标准的相邻加成，但雨林单元格使所有专业化区域-1对应相邻加成。+1 [ICON_TradeRoute] 贸易路线容量，拥有制陶术科技后获得1个免费商人。商人首次进入克里城市3个单元格内的无主单元格后，这些单元格将划入克里领土中。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_CREE_TRADE_GAIN_TILES_DESCRIPTION",		        "Specialty districts gain a Standard adjacency bonus from Forest tiles, however, -1 corresponding adjacency bonus from Jungle tiles. +1 [ICON_TradeRoute] Trade Route capacity and a free Trader with the Pottery technology. Unclaimed tiles within 3 tiles of a Cree city come under Cree control when a Trader first moves into them."	),
("zh_Hans_CN",	"LOC_ABILITY_IGNORE_WOOD_DESCRIPTION",		        "视野可以穿过树林和雨林，无视树林和雨林的 [ICON_Movement] 移动力惩罚。"		),
("en_US",	    "LOC_ABILITY_IGNORE_WOOD_DESCRIPTION",		        "Can see through Forest and Jungle, have faster [ICON_Movement] Movement on Forest and Jungle. "	),
("zh_Hans_CN",  "LOC_UNIT_CREE_OKIHTCITAW_DESCRIPTION",           "远古时代的克里特色单位，替代侦察兵。视野为3。[NEWLINE][NEWLINE]初始便拥有1次免费升级。"),
("en_US",       "LOC_UNIT_CREE_OKIHTCITAW_DESCRIPTION",           "Cree unique Ancient era Recon unit that replaces the Scout. Sight: 3 [NEWLINE][NEWLINE]Starts with 1 free promotion."),
--CIVILIZATION_EGYPT
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_ITERU_EXPANSION2_DESCRIPTION",		"游戏开始时便拥有“灌溉”科技。在河流旁建造区域与奇观时+40% [ICON_Production] 生产力。泛滥平原单元格上的农场额外+1 [ICON_Food] 食物 +1 [ICON_Production] 生产力。在河流旁的资源获得双倍产出。不会受到洪水的损害。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_ITERU_EXPANSION2_DESCRIPTION",		"Begin the game with tech 'Irrigation' unlocked. +40% [ICON_Production] Production towards districts and wonders if placed next to a River. +1 [ICON_Food] Food +1 [ICON_Production] Production for Farm if it is on a Floodplain tile. Resources adjacent to a River provide double yields. Does not receive damage from Floods."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MEDITERRANEAN_EXPANSION2_DESCRIPTION",	"其他文明无法对你发起正式战争。	您通往其他文明的 [ICON_TradeRoute] 贸易路线为起源城市+1 [ICON_Food] 食物，+1 [ICON_Production] 生产力，+2 [ICON_Gold] 金币。若该文明是你的同盟，则这些产出翻倍。与盟友进行贸易可获得双倍同盟点数。"		),
("en_US",	    "LOC_TRAIT_LEADER_MEDITERRANEAN_EXPANSION2_DESCRIPTION",	"Formal wars cannot be declared on Egypt. Your [ICON_TradeRoute] Trade Routes to other civilizations provide +1 [ICON_Food] Food, +1 [ICON_Production] Production, and +2 [ICON_Gold] Gold for Egypt. If that civilization is your ally, double these trade routes bonus. Trading with Allies earns twice as many bonus Alliance Points."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_SPHINX_EXPANSION2_DESCRIPTION",		    "解锁建造者建造埃及特色改良设施“狮身人面像”的能力。[NEWLINE][NEWLINE]+1 [ICON_Faith] 信仰值、+1 [ICON_Culture] 文化值、+2魅力。如建在沙漠或泛滥平原上，则额外+1 [ICON_Faith] 信仰值、+1 [ICON_Culture] 文化值。每与一个奇观相邻时额外 +3 [ICON_Faith] 信仰值，每与一个沙漠单元相邻时额外+1 [ICON_Culture] 文化值。[NEWLINE]解锁“神学”和“自然历史”后将产出额外[ICON_Faith] 信仰值和 [ICON_Culture] 文化值。解锁“飞行”后将提供 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]每座城市只能修建一个。"		),
("en_US",	    "LOC_IMPROVEMENT_SPHINX_EXPANSION2_DESCRIPTION",		    "Unlocks the Builder ability to construct a Sphinx, unique to Egypt.[NEWLINE][NEWLINE]+1 [ICON_Faith] Faith and +1 [ICON_Culture] Culture, +2 Appeal. +1 [ICON_Faith] Faith and +1 [ICON_Culture] Culture if build on desert or on floodplains. Provides extra +3 [ICON_Faith] Faith from each Wonder and +1 [ICON_Culture] Culture from each desert tile it is adjacent to.[NEWLINE] Provides extra [ICON_Faith] Faith from 'Theology' and [ICON_Culture] Culture from 'Natural History'. Provides [ICON_TOURISM] Tourism after researching 'Flight'. [NEWLINE][NEWLINE]Can be built only once in each city."	),
("zh_Hans_CN",  "LOC_UNIT_EGYPTIAN_CHARIOT_ARCHER_DESCRIPTION",           "埃及特色的远古时代远程单位，替代重型战车，克制近战单位。视野为1。[NEWLINE][NEWLINE]从开阔地形开始时， [ICON_Movement] 移动力为4。"),
("en_US",       "LOC_UNIT_EGYPTIAN_CHARIOT_ARCHER_DESCRIPTION",           "Egyptian unique Ancient era Ranged unit that replaces the Heavy Chariot, counters Melee units well. Sight: 1 [NEWLINE][NEWLINE]4 [ICON_Movement] Movement when starting in open terrain."),
--CIVILIZATION_ENGLAND
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_PAX_BRITANNICA_EXPANSION2_DESCRIPTION",		"在非首都所在大陆建造城市时免费获得1个近战单位，若为该大陆的首座城市则额外+2 [ICON_TradeRoute] 贸易路线容量。研究“膛线”科技后，获得特色单位“红衫军”。"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_PAX_BRITANNICA_EXPANSION2_DESCRIPTION",		"Each time you found a city on a continent other than your home continent receive a free melee unit, +2 [ICON_TradeRoute] Trade Route capacity if this is your first city on that continent. Gain the Redcoat unique unit when the 'Rifiling' technology is researched."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_INDUSTRIAL_REVOLUTION_DESCRIPTION",		    "建造工业区和工业区建筑时+100% [ICON_PRODUCTION] 生产力。建造工业区可触发文化炸弹。解锁文艺复兴时期后期的前置科技后，立即获得工业时代早期的科技。在非首都大陆上的建立沿海城市时获得额外+5 [ICON_Citizen] 人口，这些城市+15%所有类型产出。"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_INDUSTRIAL_REVOLUTION_DESCRIPTION",		    "+100% [ICON_PRODUCTION] Production towards Industrial Zone and it's buildings. Completing an Industrial Zone may triggers a Culture Bomb. Grant free industrial era technologies upon finishing their pre-techs on Renaissance era. +5 [ICON_Citizen] citizens when you establish a coastal city out of your home continent, and +15% all types of yields for foreign coastal cities."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_INDUSTRIAL_REVOLUTION_NAME",		        "日不落帝国"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_INDUSTRIAL_REVOLUTION_NAME",		        "The Empire on which The Sun Never Sets"	),
("zh_Hans_CN",	"LOC_DISTRICT_ROYAL_NAVY_DOCKYARD_EXPANSION2_DESCRIPTION",		    "城市中的英国特色海军活动区域，取代港口。同时移除该单元格的上船、下船 [ICON_Movement] 移动力减益。必须建在与陆地相邻的海岸或湖泊地形上。无法建造在礁石上。[NEWLINE][NEWLINE]与港口的区别在于，从海洋资源获得超大量 [ICON_Gold] 金币相邻加成，但不从陆地资源和运河获得相邻加成。建造完成后，获得可生产的最强海军单位。建造的所有海军单位+1 [ICON_Movement] 移动力。修建在外国大陆上时每回合的忠诚度+10。防御工事的攻击范围为2。不会影响相邻单元格的魅力值。"	),
("en_US",	    "LOC_DISTRICT_ROYAL_NAVY_DOCKYARD_EXPANSION2_DESCRIPTION",		    "A district unique to England for naval activity in your city. Replaces the Harbor district. Also removes the [ICON_Movement] Movement penalty for embarking and disembarking to and from this tile. Must be built on Coast or Lake Terrain adjacent to land. Cannot be built on Reef.[NEWLINE][NEWLINE]Gain tremendous [ICON_Gold] gold adjacency bonus from sea resource. Grants a copy of the strongest naval unit you can build upon constructed. +1 [ICON_Movement] Movement for all naval units built in Dockyard. +10 Loyalty per turn when built on a foreign continent. Attack range of defence is 2. No appeal penalty."	),
("zh_Hans_CN",  "LOC_UNIT_ENGLISH_REDCOAT_DESCRIPTION",           "维多利亚作为领袖时的英国特色的工业时代近战单位，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]在除您首都所在大陆以外的大陆作战时，+10 [ICON_Strength] 战斗力。没有下船消耗。"),
("en_US",       "LOC_UNIT_ENGLISH_REDCOAT_DESCRIPTION",           "English unique Industrial era Melee unit when Victoria is their leader, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength when fighting on a continent other than that of your capital's. No disembark cost."),
--CIVILIZATION_ETHIOPIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_ETHIOPIA_DESCRIPTION",		        "埃塞俄比亚的国际贸易路线起点处每拥有一种资源，其提供的 [ICON_Faith] 信仰值便+0.5。城市每拥有一种重复的改良资源，其提供的 [ICON_Faith] 信仰值便+1。每座考古博物馆可保存6个 [ICON_GreatWork_Artifact] 文物而不是3个，建成后可支撑当前城市考古学家额限为两位。有6个 [ICON_GreatWork_Artifact] 文物时，考古博物馆自动获得主题化奖励。可用信仰值购买考古博物馆和考古学家。"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_ETHIOPIA_DESCRIPTION",		        "Ethiopia’s International Trade Routes grant +0.5 [ICON_Faith] Faith per resource at the origin. Improved resources provide +1 [ICON_Faith] Faith for each copy the city owns. Each Archaeological Museum holds 6 [ICON_GreatWork_Artifact] Artifacts instead of 3, and can support 2 Archaeologists at once. Archaeological Museums are automatically themed when they have 6 [ICON_GreatWork_Artifact] Artifacts. Can purchase Archaeological Museums and Archaeologists with [ICON_Faith] Faith."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MENELIK_DESCRIPTION",		        "获得等同于建在丘陵上的城市所产生 [ICON_FAITH] 信仰值15%的 [ICON_SCIENCE] 科技值和 [ICON_CULTURE] 文化值。有 [ICON_Governor] 总督就职的城市中，建筑修建完成时可获得等同修建所消耗生产力20%的 [ICON_Faith] 信仰值，但所有城市的 [ICON_Gold] 金币产出-20%。单位在丘陵上时+4 [ICON_STRENGTH] 战斗力。"	),
("en_US",	    "LOC_TRAIT_LEADER_MENELIK_DESCRIPTION",		        "Receive [ICON_SCIENCE] Science and [ICON_CULTURE] Culture equal to 15% of your [ICON_FAITH] Faith generation in cities founded on Hills. Gain [ICON_FAITH] Faith equal to 20% of the construction cost when finishing buildings in city with a [ICON_Governor] governor, but -20% [ICON_Gold] gold for all cities. Units receive +4 [ICON_STRENGTH] Combat Strength on Hills."	),
("zh_Hans_CN",  "LOC_UNIT_ETHIOPIAN_OROMO_CAVALRY_EXPANSION2_DESCRIPTION",           "埃塞俄比亚特色的中世纪轻骑兵单位，替代追猎者，克制攻城单位。[NEWLINE]视野为3。[NEWLINE][NEWLINE]战斗力更强、视野更广。在丘陵中移动时无 [ICON_Movement] 移动力减益。"),
("en_US",       "LOC_UNIT_ETHIOPIAN_OROMO_CAVALRY_EXPANSION2_DESCRIPTION",           "Ethiopian unique Medieval era Light Cavalry unit, counters Siege units well. [NEWLINE]Sight: 3 [NEWLINE][NEWLINE] Stronger and greater sight than the Courser that it replaces. Receives no [ICON_Movement] Movement penalty from moving in Hills."),
--CIVILIZATION_FRANCE
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_WONDER_TOURISM_DESCRIPTION",	"为中世纪以后的世界奇观+100% [ICON_Production] 生产力。 所有世界奇观的 [ICON_Tourism] 旅游业绩+100%。建成世界奇观后奖励一个近战单位。世界奇观+5 [ICON_Culture] 文化值，+5 [ICON_Gold] 金币和+2 [ICON_Amenities] 宜居度。"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_WONDER_TOURISM_DESCRIPTION",	"+100% [ICON_Production] Production toward world wonders from Medieval era to Information era. [ICON_Tourism] Tourism from wonders of any era is +100%. Receive a Melee unit once a world wonder is finished. World wonders provide +5 [ICON_Culture] Culture, +5 [ICON_Gold] Gold and +2 [ICON_Amenities] Amenities."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_FLYING_SQUADRON_DESCRIPTION",		    "[ICON_VisLimited] 外交能见度比遇到的其他文明高1级。[NEWLINE]解锁“城堡”科技后免费获得1个间谍（并增加1个间谍容量）。所有间谍初始便可免费升级为探员。训练间谍时+50% [ICON_Production] 生产力，间谍行动的时间缩短25%。"	),
("en_US",	    "LOC_TRAIT_LEADER_FLYING_SQUADRON_DESCRIPTION",		    "Has 1 level of [ICON_VisLimited] Diplomatic Visibility greater than normal with every civilization that she's met. Receives a free Spy (and extra spy capacity) with the Castles technology. All spies start as Agents with a free promotion. +50% [ICON_Production] Production toward Spies. Spy operations take 25% less time."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ELEANOR_LOYALTY_DESCRIPTION",		    "埃莉诺的城市中每个巨作可使9个单元格内的外国城市每回合减少1点忠诚度。城市因失去忠诚度而离开另一文明后，如其每回合对埃莉诺的忠诚度最高，则会跳过成为自由城市阶段直接加入埃莉诺的文明。[ICON_Culture] 文化值相邻加成不少于4的剧院广场额外获得+2 [ICON_GreatWriter] 大作家，[ICON_GreatArtist] 大艺术家，[ICON_GreatMusician] 大音乐家点数。"	),
("en_US",	    "LOC_TRAIT_LEADER_ELEANOR_LOYALTY_DESCRIPTION",		    "Great Works in Eleanor’s cities each cause -1 Loyalty per turn in foreign cities within 9 tiles. A city that leaves another civilization due to a loss of Loyalty and is currently receiving the most Loyalty per turn from Eleanor's civilization skips the Free City step to join this civilization. +2 [ICON_GreatWriter] Writer, [ICON_GreatArtist] Artist, and [ICON_GreatMusician] Musician if city has a theater district which [ICON_Culture] culture adjacency bonus is no less than 4."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_CHATEAU_EXPANSION2_DESCRIPTION",		"解锁建造者建造法国特色建筑法国城堡的能力。[NEWLINE][NEWLINE]+2 [ICON_Culture] 文化值、+1 [ICON_Gold] 金币。+1魅力。每与一座奇观相邻便+2 [ICON_Culture] 文化值。如处在包含河岸的单元格上，则+2 [ICON_Gold] 金币。研究完“飞行”之后，将根据 [ICON_CULTURE] 文化值提供 [ICON_TOURISM] 旅游业绩。必须建在加成或奢侈品资源旁。无法建在另一座法国城堡旁。"	),
("en_US",	    "LOC_IMPROVEMENT_CHATEAU_EXPANSION2_DESCRIPTION",		"Unlocks the Builder ability to construct a Château, unique to France.[NEWLINE][NEWLINE]+2 [ICON_Culture] Culture and +1  [ICON_Gold] Gold. +1 Appeal. +2 [ICON_Culture] Culture for every adjacent wonder. +2 [ICON_Gold] Gold if on a tile containing a River edge. Provides [ICON_TOURISM] Tourism from [ICON_CULTURE] Culture after researching Flight. Must be placed adjacent to a Bonus or Luxury resource. Cannot be built adjacent to another Château."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MAGNIFICENCES_DESCRIPTION",		        "每种奢侈品资源只要1份即可为您的所有城市提供 [ICON_Amenities] 宜居度。与剧院广场区域或法国特色城堡相邻的改良奢侈品资源提供的 [ICON_CULTURE] 文化值+2。建造剧院广场和其建筑时+100% [ICON_PRODUCTION] 生产力。艺术博物馆有3个巨作即可自动获得主题化奖励。可在拥有剧院广场区域的任意城市中开展“宫廷盛会”项目。"	),
("en_US",	    "LOC_TRAIT_LEADER_MAGNIFICENCES_DESCRIPTION",		        "One copy of each luxury resource is enough to provide [ICON_Amenities] Amenity to all cities. Improved Luxury resources adjacent to Theater Square districts or Châteaux receive +2 [ICON_CULTURE] Culture. +100% [ICON_PRODUCTION] Production towards Theater and it's buildings. Art Museums are automatically themed when they have 3 GreatWorks. May initiate the Court Festival project in any city with a Theater Square district."	),
("zh_Hans_CN",  "LOC_UNIT_FRENCH_GARDE_IMPERIALE_DESCRIPTION",           "法国特色的工业时代近战单位，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]在您首都大陆作战时，+10 [ICON_Strength] 战斗力。可通过击杀单位而获得 [ICON_GreatGeneral] 大将军点数。"),
("en_US",       "LOC_UNIT_FRENCH_GARDE_IMPERIALE_DESCRIPTION",           "French unique Industrial era Melee unit, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength when fighting on your capital's continent. [ICON_GreatGeneral] Great General points for killing units."),
--CIVILIZATION_GAUL
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GAUL_DESCRIPTION",		        "矿山为所有区域提供标准相邻加成、对无主领土施放文化炸弹，并+1 [ICON_CULTURE] 文化值。将政体中的一个军事、经济、外交槽位转为通用槽位。专业化区域无法建在市中心旁。"	),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GAUL_DESCRIPTION",		        "Mines provide a standard adjacency bonus for all districts, a Culture Bomb of unowned territory, and receive +1 [ICON_CULTURE] Culture. One Military, Economic, and Diplomatic policy slot in the current government is converted to a Wildcard slot. Specialty districts cannot be built adjacent to the City Center."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_AMBIORIX_DESCRIPTION",		        "生产出一个非平民单位时，您的文明获得等同于该单位花费20%的 [ICON_CULTURE] 文化值。每与一个单位相邻，近战、抗骑兵和远程单位便+2 [ICON_Strength] 战斗力。建造军营区建筑后，城市+2 [ICON_CITIZEN] 人口。"	),
("en_US",	    "LOC_TRAIT_LEADER_AMBIORIX_DESCRIPTION",		        "Your civilization gains [ICON_CULTURE] Culture equal to 20% of the unit’s cost when a non civilian is trained. Melee, anti-cavalry, and ranged units receive +2 [ICON_Strength] Combat Strength for every adjacent combat unit. +2 [ICON_CITIZEN] citizens in city upon completion of Encampment buildings."	),
("zh_Hans_CN",	"LOC_DISTRICT_OPPIDUM_DESCRIPTION",		        "高卢特色区域，替代“工业区”。[NEWLINE][NEWLINE]与工业区的区别在于，与采石场改良设施或战略资源相邻，获得大量 [ICON_Production] 生产力加成，但不从水渠、堤坝、运河、航空港获得相邻加成。“奥皮杜姆”区域还可以发动远程攻击，防御工事的攻击范围为2，建造首个“奥皮杜姆”后立即解锁“学徒”科技。不会影响相邻单元格的魅力值。"		),
("en_US",	    "LOC_DISTRICT_OPPIDUM_DESCRIPTION",		        "A district unique to Gaul that replaces the Industrial Zone. [NEWLINE][NEWLINE]Provide large [ICON_Production] Production bonus if adjacent to a Quarry improvement or strategic resource. The Oppidum district is defensible with a ranged attack, attack range of defence is 2. When the first Oppidum is constructed the Apprenticeship technology is unlocked. No appeal penalty."	),
("zh_Hans_CN",  "LOC_UNIT_GAUL_GAESATAE_DESCRIPTION",           "高卢特色远古时代单位，替代勇士，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]与基础战斗力更高的单位作战时+10 [ICON_Strength] 战斗力。进攻区域防御时+5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_GAUL_GAESATAE_DESCRIPTION",           "Gallic unique Ancient era unit that replaces the Warrior, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]This unit receives +10 [ICON_Strength] Combat Strength when fighting units with a higher base Combat Strength. +5 [ICON_Strength] Combat Strength vs. district defenses."),
--CIVILIZATION_GERMANY
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_IMPERIAL_FREE_CITIES_DESCRIPTION",		"每座城市都能建造比平常多一个区域（无视 [ICON_Citizen] 人口数量限制）。与其他文明和平时，友方境内单位+1 [ICON_Movement] 移动力。与其他文明处于战争时，敌方境内单位+1 [ICON_Movement] 移动力。[ICON_POWER] 电力供给充足时，建筑来自电力的额外产出+3。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_IMPERIAL_FREE_CITIES_DESCRIPTION",		"Each city can build one more district than usual (exceeding the normal limit based on [ICON_Citizen] Population). +1 [ICON_Movement] Movement for units in friendly territory if you are in peace with other major civilizations,  +1 [ICON_Movement] Movement for units in enemy territory if you are in war with any major civilization. Buildings that provide additional yields when [ICON_POWER] Powered receive +3 of that yield. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_HOLY_ROMAN_EMPEROR_DESCRIPTION",		        "额外的军事政策槽位。与城邦或自由城市战斗时，所有单位+10 [ICON_Strength] 战斗力。建造军营后奖励一个近战单位。"		),
("en_US",	    "LOC_TRAIT_LEADER_HOLY_ROMAN_EMPEROR_DESCRIPTION",		        "Additional Military policy slot. +10 [ICON_Strength] Combat Strength against city-states and free city. Get a free Melee unit upon finishing a Encampment district."	),
("zh_Hans_CN",	"LOC_DISTRICT_HANSA_EXPANSION2_DESCRIPTION",		            "德国特色工业区域，替代工业区，建造费用更低。[NEWLINE][NEWLINE]与工业区的区别在于，与商业中心区域和资源相邻时，可获得大量 [ICON_Production] 生产力加成，但不从矿山、采石场、伐木场、油井、海上石油钻机和战略资源获得相邻加成。专家产出更多 [ICON_Production] 生产力。额外获得1点 [ICON_GreatScientist] 大科学家和 [ICON_GreatMERCHANT] 大商人点数。可靠近市中心建造，不会影响相邻单元格的魅力值。"		),
("en_US",	    "LOC_DISTRICT_HANSA_EXPANSION2_DESCRIPTION",		            "A district unique to Germany for industrial activity. Replaces the Industrial Zone district and cheaper to build.[NEWLINE][NEWLINE]Provide large [ICON_Production] Production bonus for each adjacent Commercial Hub and adjacent resource. More [ICON_Production] production from specialists. Extra +1 [ICON_GreatScientist] Great Scientist and [ICON_GreatMERCHANT] Great Merchant point. Can be built near city center, no appeal penalty."	),
("zh_Hans_CN",	"LOC_PROMOTION_COMBAT_VS_CITY_STATES_DESCRIPTION",		        "对抗城邦或自由城市时+10 [ICON_Strength] 战斗力。"		),
("en_US",	    "LOC_PROMOTION_COMBAT_VS_CITY_STATES_DESCRIPTION",		        "+10 [ICON_Strength] Combat Strength vs. City-States or Free city."	),
("zh_Hans_CN",	"LOC_ABILITY_BARBAROSSA_COMBAT_BONUS_VS_CITY_STATES_DESCRIPTION",		        "对抗城邦或自由城市时+10 [ICON_Strength] 战斗力。"		),
("en_US",	    "LOC_ABILITY_BARBAROSSA_COMBAT_BONUS_VS_CITY_STATES_DESCRIPTION",		        "+10 [ICON_Strength] Combat Strength vs. City-States or Free city."	),
("zh_Hans_CN",	"LOC_ABILITY_GERMANY_WAR_ENEMY_TERRITORY_MOVEMENT_DESCRIPTION",		        "与其他文明处于战争时，敌方境内单位+2 [ICON_Movement] 移动力。"		),
("en_US",	    "LOC_ABILITY_GERMANY_WAR_ENEMY_TERRITORY_MOVEMENT_DESCRIPTION",		        "+2 [ICON_Movement] Movement for units in enemy territory if you are in war with any major civilization."	),
--CIVILIZATION_GEORGIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GOLDEN_AGE_QUESTS_DESCRIPTION",		            "游戏开始时便获得“砌砖”科技和“城堡” [ICON_TechBoosted] 尤里卡。在黄金或英雄时代初期设置着力点时，除原有的其他加成外还将获得提高时代得分的普通时代加成。你可以使用 [ICON_Faith] 信仰值购买任何建筑。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GOLDEN_AGE_QUESTS_DESCRIPTION",		            "Get free tech 'Masonry' and [ICON_TechBoosted] boost for 'Castles' at the beginning of the game. When making Dedications at the beginning of a Golden Age or Heroic Age, receive the Normal Age bonus towards improving Era Score in addition to the other bonus. You can purchase any building with [ICON_Faith] faith."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_RELIGION_CITY_STATES_DESCRIPTION",		            "[ICON_Governor] 总督就职的城市中每位 [ICON_Citizen] 公民将+1点 [ICON_Faith] 信仰值。战斗胜利后，获得等同于被击杀单位战斗力50%的 [ICON_FAITH] 信仰值（标准速度下）。如城邦以己方宗教为主流宗教，则派遣至其中每名 [ICON_ENVOY] 使者将视为2名 [ICON_ENVOY] 使者。（必须拥有1个主流宗教。） "		),
("en_US",	    "LOC_TRAIT_LEADER_RELIGION_CITY_STATES_DESCRIPTION",		            "+1 [ICON_Faith] Faith per [ICON_Citizen] Citizen in cities with [ICON_Governor] Governors. Combat victories provide [ICON_FAITH] Faith equal to 50% of the Combat Strength of the defeated unit (on Standard Speed). Each [ICON_ENVOY] Envoy you send to a city-state of your majority Religion counts as two [ICON_ENVOY] Envoys.(Must have a majority Religion).  "	),
("zh_Hans_CN",	"LOC_BUILDING_TSIKHE_DESCRIPTION_XP2",		                            "格鲁吉亚特色建筑。拥有更强的外部防御力。+4 [ICON_Faith] 信仰值，+1 [ICON_GreatWork_Relic] 遗物槽位。进入黄金时代后 [ICON_TOURISM] 旅游业绩与 [ICON_FAITH] 信仰值均+100%。此城对相邻城市释放的宗教压力加倍。获得“保护地球”市政后提供+3点 [ICON_TOURISM] 旅游业绩。 "		),
("en_US",	    "LOC_BUILDING_TSIKHE_DESCRIPTION_XP2",		                            "A building unique to Georgia. Higher outer defenses than Medieval Walls. +4 [ICON_Faith] Faith, +1 [ICON_GreatWork_Relic] Relic Slot. When in a Golden Age the [ICON_TOURISM] Tourism and [ICON_FAITH] Faith are +100%. Double the religious pressure released from this city. Provides +3 [ICON_TOURISM] Tourism after advancing to the Conservation Civic. "	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GOLDEN_AGE_QUESTS_MODE_DESCRIPTION",		    "游戏开始时便获得“砌砖”科技和“城堡” [ICON_TechBoosted] 尤里卡。进入黄金时代后将额外获得一个通配符政策槽位，并能使用黑暗时代政策。你可以使用 [ICON_Faith] 信仰值购买任何建筑。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GOLDEN_AGE_QUESTS_MODE_DESCRIPTION",		    "Get free tech 'Masonry' and [ICON_TechBoosted] boost for 'Castles' at the beginning of the game. While in a Golden Age, gains an extra Wildcard Policy Slot, and may use Dark Age Policies. You can purchase any building with [ICON_Faith] faith."	),
("zh_Hans_CN",  "LOC_UNIT_GEORGIAN_KHEVSURETI_DESCRIPTION",           "格鲁吉亚特色的中世纪近战单位，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]在丘陵地形战斗时+7 [ICON_Strength] 战斗力。不受丘陵地形的 [ICON_Movement] 移动力减益影响。"),
("en_US",       "LOC_UNIT_GEORGIAN_KHEVSURETI_DESCRIPTION",           "Georgian unique Medieval Era unit Melee unit, counters Anti-Cavalry units well. Sight: 1 [NEWLINE][NEWLINE]+7 [ICON_Strength] Combat Strength bonus when fighting in hill terrain. No [ICON_Movement] Movement penalty in hill terrain."),
--CIVILIZATION_GRAN_COLOMBIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_EJERCITO_PATRIOTA_DESCRIPTION",		"所有单位+1 [ICON_Movement] 移动力。升级单位不会结束其回合。无论处于 [ICON_GLORY_GOLDEN_AGE] 黄金时代或 [ICON_GLORY_DARK_AGE] 黑暗时代，每个 [ICON_CITIZEN] 公民施加的忠诚度压力均与普通时代相同。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_EJERCITO_PATRIOTA_DESCRIPTION",		"+1 [ICON_Movement] Movement to all units. Promoting a unit does not end that unit’s turn. Your [ICON_CITIZEN] citizen identity on [ICON_GLORY_GOLDEN_AGE] golden age and [ICON_GLORY_DARK_AGE] dark age are the same as that in a Normal age. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_CAMPANA_ADMIRABLE_DESCRIPTION_XP1",		"进入新时代后获得1名总指挥。若你解放了城市，则10回合内所有类型产出+50%。击杀单位可获得等同于被击杀单位 [ICON_Strength] 战斗力50%的 [ICON_Gold] 金币。"		),
("en_US",	    "LOC_TRAIT_LEADER_CAMPANA_ADMIRABLE_DESCRIPTION_XP1",		"Earn a Comandante General when the game enters a new era. +50% for all types of yields once you have liberated a city within 10 turns. Combat victories provide [ICON_Gold] Gold equal to 50% of the [ICON_Strength] Combat Strength of the defeated unit."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_HACIENDA_DESCRIPTION",		"解锁建造者建造大哥伦比亚特色建筑大庄园的能力。[NEWLINE][NEWLINE]+1 [ICON_GOLD] 金币、+1 [ICON_PRODUCTION] 生产力并+1 [Icon_Housing] 住房。[NEWLINE]每与1座种植园相邻则+1 [ICON_Food] 食物。解锁科技“经济”后，每与1座种植园相邻则+1 [ICON_Gold] 金币。解锁科技“零件规格化”后，每与1座种植园相邻则+1 [ICON_Production] 生产力。[NEWLINE]每与1座大庄园相邻，大庄园便+1 [ICON_Gold] 金币。获得市政“紧急部署”后，每与1座大庄园相邻便+1 [ICON_Production] 生产力。[NEWLINE][NEWLINE]只能建造在平原、平原丘陵、草原和草原丘陵上。"		),
("en_US",	    "LOC_IMPROVEMENT_HACIENDA_DESCRIPTION",		"Unlocks the Builder ability to construct a Hacienda, unique to Gran Colombia.[NEWLINE][NEWLINE]+1 [ICON_GOLD] Gold, +1 [ICON_PRODUCTION] Production, and +1 [Icon_Housing] Housing. [NEWLINE]+1 [ICON_Food] Food for every adjacent Plantations. +1 [ICON_Gold] Gold for every adjacent Plantations with 'Economics'. +1 [ICON_Production] Production for every adjacent Plantations with 'Replaceable Parts'. [NEWLINE]Haciendas receive +1 [ICON_Gold] Gold for every adjacent Haciendas. Haciendas receive +1 [ICON_Production] Production for every adjacent Haciendas with 'Rapid Deployment'. [NEWLINE][NEWLINE]Can only be built on Plains, Plains Hills, Grassland, and Grassland Hills."	),
("zh_Hans_CN",  "LOC_UNIT_COLOMBIAN_LLANERO_DESCRIPTION",           "大哥伦比亚特色的工业时代轻骑兵单位，替代骑兵，克制攻城单位。视野为2。[NEWLINE][NEWLINE]维护花费较低。每与1个牛仔骑兵相邻，则+4 [ICON_Strength] 战斗力。总指挥发动“隐退”技能时，若处于有效范围内，将完全回复生命值。"),
("en_US",       "LOC_UNIT_COLOMBIAN_LLANERO_DESCRIPTION",           "Gran Colombian Industrial era Light Cavalry unique unit that replaces the Cavalry, counters Siege units well. Sight: 2 [NEWLINE][NEWLINE] Low maintenance cost. +4 [ICON_Strength] Combat Strength for every adjacent Llanero. Fully heals when in range of a Comandante General that activates its retire ability."),
--CIVILIZATION_GREECE
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_PLATOS_REPUBLIC_DESCRIPTION",		"每种政体可获得一个额外的通配符槽位。市政广场及市政广场建筑额外获得1个 [ICON_Governor] 总督头衔。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_PLATOS_REPUBLIC_DESCRIPTION",		"One extra Wildcard policy slot in any government. Gain extra [ICON_Governor] Governor title from Government District and its buildings."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_THERMOPYLAE_DESCRIPTION",		            "战斗胜利可获得等同于被击败单位 [ICON_Strength] 战斗力50%的 [ICON_Culture] 文化值。槽位中每有一项军事政策，则+1 [ICON_Strength] 战斗力。军营区额外+3 [ICON_Housing] 住房。"		),
("en_US",	    "LOC_TRAIT_LEADER_THERMOPYLAE_DESCRIPTION",		            "Combat victories provide [ICON_Culture] Culture equal to 50% of the [ICON_Strength] Combat Strength of the defeated unit. +1 [ICON_Strength] Combat Strength for every Military Policy slotted. +3 [ICON_Housing] Housing for Encampment district."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_SURROUNDED_BY_GLORY_DESCRIPTION",		    "作为宗主国时，每个下辖的城邦使您的 [ICON_Culture] 文化值+5%。修建远古时代和古典时代的世界奇观时+100% [ICON_PRODUCTION] 生产力。与世界奇观相邻的单元格+1 [ICON_Culture] 文化值和+1 [ICON_Gold] 金币。"		),
("en_US",	    "LOC_TRAIT_LEADER_SURROUNDED_BY_GLORY_DESCRIPTION",		    "+5% [ICON_Culture] Culture per city-state you are the Suzerain of. +100% [ICON_PRODUCTION] production toward wonders on ancient era and classical era. +1 [ICON_Culture] Culture and +1 [ICON_Gold] Gold for tiles that are adjacent to World wonders. "	),
("zh_Hans_CN",	"LOC_DISTRICT_ACROPOLIS_EXPANSION2_DESCRIPTION",		    "希腊特色文化遗址区域，替代剧院广场，建造费用更低。[NEWLINE][NEWLINE]与剧院广场的区别在于，每个相邻的区域提供标准 [ICON_Culture] 文化值加成，且建成后奖励1名 [ICON_Envoy] 使者。只能建在丘陵上。[NEWLINE]为相邻单元格提供+1魅力。"		),
("en_US",	    "LOC_DISTRICT_ACROPOLIS_EXPANSION2_DESCRIPTION",		    "A district unique to Greece for cultural sites. Replaces the Theater Square district and cheaper to build. [NEWLINE][NEWLINE]Awards 1 [ICON_Envoy] Envoy when completed. Gain standard [ICON_Culture] Culture bonus from each adjacent district. Can only be built on Hills.[NEWLINE]+1 Appeal."	),
("zh_Hans_CN",  "LOC_UNIT_GREEK_HOPLITE_DESCRIPTION",           "希腊特色的远古时代抗骑兵单位，替代枪兵，克制骑兵单位。视野为1。[NEWLINE][NEWLINE]与至少一个重装步兵单位相邻时+10 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_GREEK_HOPLITE_DESCRIPTION",           "Greek unique Ancient era Anti-Cavalry unit that replaces the Spearman, counters Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength if there is at least one adjacent Hoplite unit."),
--CIVILIZATION_HUNGRAY
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_PEARL_DANUBE_DESCRIPTION",		"在市中心河对岸且与市中心相邻的单元格修建区域与建筑时+100% [ICON_PRODUCTION] 生产力。建在河流旁的新城市额外获得+1 [ICON_Citizen] 人口。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_PEARL_DANUBE_DESCRIPTION",		"+100% [ICON_PRODUCTION] Production to Districts and Buildings constructed across a river from a City Center. +1 [ICON_Citizen] citizen for new city that has a river."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_RAVEN_KING_DESCRIPTION",		        "征集单位+2 [ICON_Movement] 移动力、+5 [ICON_Strength] 战斗力。升级征集单位所花费的 [ICON_GOLD] 金币和资源减少75%。创立万神殿或添加宗教信条时奖励2名 [ICON_ENVOY] 使者。征集城邦军队时可在该城邦增加1名 [ICON_ENVOY] 使者。研究“城堡”科技后获得特色单位“黑军”。"		),
("en_US",	    "LOC_TRAIT_LEADER_RAVEN_KING_DESCRIPTION",		        "Levied units gain an ability giving them +2 [ICON_Movement] Movement and +5 [ICON_Strength] Combat Strength. It costs 75% less [ICON_GOLD] Gold and resources to upgrade Levied units. Receive 2 [ICON_ENVOY] Envoys when you set up a Panthon or a new Religious beliefs . If you Levy troops from a City-State receive 1 [ICON_ENVOY] Envoys with that City-State. Gain the Black Army unique unit when the Castles technology is researched."	),
("zh_Hans_CN",	"LOC_BUILDING_THERMAL_BATH_DESCRIPTION",		        "匈牙利特色建筑。[ICON_Amenities] 宜居度和 [ICON_PRODUCTION] 生产力可扩展到6个单元格内的每个市中心(此加成1座城市仅能应用1次)。+6 [ICON_TOURISM] 旅游业绩。此城中若包含至少1处地热裂缝，则该城市的 [ICON_TOURISM] 旅游业绩+3，同时 [ICON_AMENITIES] 宜居度额外+2。"		),
("en_US",	    "LOC_BUILDING_THERMAL_BATH_DESCRIPTION",		        "A building unique to Hungary. Bonus [ICON_Amenities] Amenity and [ICON_PRODUCTION] Production extends to each city center within 6 tiles. This bonus applies once to a city. +6 [ICON_TOURISM] Tourism.[NEWLINE][NEWLINE]This city receives +3 [ICON_TOURISM] Tourism and +2 additional [ICON_AMENITIES] Amenities if there is at least one Geothermal Fissure in this city's borders."	),
("zh_Hans_CN",  "LOC_UNIT_HUNGARY_BLACK_ARMY_DESCRIPTION",           "匈牙利特色的中世纪轻骑兵单位，替代追猎者，克制攻城单位。视野为2。[NEWLINE][NEWLINE]每个相邻征集单位提供+3 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_HUNGARY_BLACK_ARMY_DESCRIPTION",           "Hungarian unique Medieval Era Light Cavalry unit that replaces the Courser, counters Siege units well. Sight: 2 [NEWLINE][NEWLINE] +3 [ICON_Strength] Combat Strength for each adjacent Levied unit."),
("zh_Hans_CN",  "LOC_UNIT_HUNGARY_HUSZAR_DESCRIPTION",           "匈牙利特色的工业时代轻骑兵单位，替代骑兵，克制攻城单位。视野为2。[NEWLINE][NEWLINE]每个有效同盟提供+3 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_HUNGARY_HUSZAR_DESCRIPTION",           "Hungarian unique Industrial era Light Cavalry unit that replaces Cavalry, counters Siege units well. Sight: 2 [NEWLINE][NEWLINE] +3 [ICON_Strength] Combat Strength for every active Alliance."),
--CIVILIZATION_INCA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GREAT_MOUNTAINS_DESCRIPTION",		"无需前置科技就可在山脉单元格上工作。山脉单元格提供+2 [ICON_PRODUCTION] 生产力，游戏进入工业时代后额外+1 [ICON_PRODUCTION] 生产力。每个相邻的梯田农场为山脉单元格+1 [ICON_FOOD] 食物。山脉地形为你的专业化区域提供标准相邻加成，但每两个相同的平坦地形为你的所有专业化区域-1对应相邻加成。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GREAT_MOUNTAINS_DESCRIPTION",		"Citizens may work on Mountain tiles without researching required tech. Mountain tiles provide +2 [ICON_PRODUCTION] Production and an additional +1 [Icon_production] Production when the game reaches the Industrial Era. +1 [ICON_FOOD] Food to Mountain tiles for every adjacent Terrace Farm. Specialty districts gain a Standard adjacency bonus from Mountain tiles, however, -1 corresponding adjacency bonus from each two adjacent flat terrains of the same type."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_PACHACUTI_QHAPAQ_NAN_DESCRIPTION",		"起始城市中的每座山脉单元格为国内 [ICON_TradeRoute] 贸易路线提供+1 [ICON_Food] 食物。解锁“对外贸易”市政后可获得印加路网改良设施。若单位在丘陵单元格上开始一个回合，则+2 [ICON_Movement] 移动力。"		),
("en_US",	    "LOC_TRAIT_LEADER_PACHACUTI_QHAPAQ_NAN_DESCRIPTION",		"Domestic [ICON_TradeRoute] Trade Routes gain +1 [ICON_Food] Food for every Mountain tile in the origin city. Gain the Qhapaq Ñan improvement when Foreign Trade civic is discovered.  Units gain +2 [ICON_Movement] Movement when starting a turn on a Hill."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_TERRACE_FARM_DESCRIPTION",		            "解锁建造者建造印加特色设施印加梯田的能力。[NEWLINE][NEWLINE]+1 [ICON_FOOD] 食物。+1 [ICON_Housing] 住房。每个相邻的山脉单元格+1 [ICON_FOOD] 食物。每个相邻的水渠区域+2 [ICON_PRODUCTION] 生产力。与淡水资源（非水渠区域）相邻时，+1 [ICON_PRODUCTION] 生产力。随着市政与科技的发展，与其他印加梯田相邻时还可额外获得 [ICON_FOOD] 食物加成。可建在草原丘陵、平原丘陵与沙漠丘陵上。"		),
("en_US",	    "LOC_IMPROVEMENT_TERRACE_FARM_DESCRIPTION",		            "Unlocks the Builder ability to construct a Terrace Farm, unique to the Inca.[NEWLINE][NEWLINE]+1 [ICON_FOOD] Food. Provides +1 [ICON_Housing] Housing. +1 [ICON_FOOD] Food for each adjacent Mountain Tile. Gain +2 [ICON_PRODUCTION] Production for each adjacent Aqueduct district. Receive +1 [ICON_PRODUCTION] Production if adjacent to Fresh Water and not an Aqueduct district. Additional [ICON_FOOD] Food as you advance through the Civics and Technology Tree for adjacency with other Terrace Farms. Can be placed on Grassland Hills, Plains Hills, and Desert Hills."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_MOUNTAIN_ROAD_DESCRIPTION",		        "解锁建造者建造印加特色改良设施印加路网的能力。[NEWLINE][NEWLINE]+1 [ICON_FOOD] 食物 +2 [ICON_Gold] 金币。其用作山脉地形上的移动通道，单位可消耗2点 [ICON_Movement] 移动力从一个点进入，再从另一个点走出。如 [ICON_TradeRoute] 贸易路线途径此处，其将从目的地的区域处获得成倍 [ICON_Gold] 金币。只能建在与山脉相邻的单元格上。无法被掠夺或移除。"		),
("en_US",	    "LOC_IMPROVEMENT_MOUNTAIN_ROAD_DESCRIPTION",		        "Unlocks the Builder ability to construct a Qhapaq Ñan, unique to Pachacuti.[NEWLINE][NEWLINE]+1 [ICON_FOOD] Food +2 [ICON_Gold] Gold. Acts as a movement portal on a mountain range, allowing units to move into it and exit from another portal at the cost of 2 [ICON_Movement] Movement. [ICON_TradeRoute] Trade Routes traveling through it can multiply the [ICON_Gold] Gold they get from districts at their destination. Can only be built on an adjacent Mountain tile. Cannot be pillaged or removed."	),
("zh_Hans_CN",  "LOC_UNIT_INCA_WARAKAQ_DESCRIPTION",           "印加中世纪特色侦察单位，替代散兵。视野为4。[NEWLINE][NEWLINE]每回合+1额外攻击。"),
("en_US",       "LOC_UNIT_INCA_WARAKAQ_DESCRIPTION",           "Incan unique Medieval Era Recon unit that replaces the Skirmisher. Sight: 4 [NEWLINE][NEWLINE]Stronger at ranged combat than the Skirmisher and +1 additional attack per turn."),
--CIVILIZATION_INDIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_DHARMA_EXPANSION2_DESCRIPTION",		"游戏开始时便获得足以创建万神殿的 [ICON_Faith] 信仰值。从城市中的每个宗教（其须拥有至少1个信徒）获得信徒信仰信条加成。城市从每个宗教（须有至少1个信徒）获得1点 [ICON_Amenities] 宜居度。传教士和使徒的传播次数+2。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_DHARMA_EXPANSION2_DESCRIPTION",		"Get some free [ICON_Faith] Faith that enough for panthon establishment at the beginning of the game. Receives Follower Belief bonuses in a city from each Religion that has at least 1 Follower. Cities gain an [ICON_Amenities] Amenity for every Religion with at least 1 Follower. Missionaries and Apostle have +2 spreads. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_SATYAGRAHA_DESCRIPTION",	                "所遇到的文明（包括印度）已创建了宗教且不在战争状态，均会+10信仰值 [ICON_Faith] 。与甘地交战的文明获得双倍厌战情绪。不能建造娱乐中心和水上乐园，圣地区、神社、寺庙和祭祀建筑分别+1、+2、+3、+4 [ICON_Amenities] 宜居度。"		),
("en_US",	    "LOC_TRAIT_LEADER_SATYAGRAHA_DESCRIPTION",                  "+10 [ICON_Faith] Faith for each civilization (including India) they have met that has founded a Religion and is not currently at war. Opposing civilizations receive double the war weariness for fighting against Gandhi. Can't build (Water) Entertainment Complex district, +1/+2/+3/+4 [ICON_Amenities] Amenities for Holy Site, Shrine, Temple, and Worship buildings instead."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ARTHASHASTRA_DESCRIPTION",	            "解锁军事训练市政后可宣布领土扩张战争。宣布领土扩张战争后的10回合内+2 [ICON_Movement] 移动力、+5 [ICON_Strength] 战斗力。建成军营区时奖励一个建造者单位，首次建成时还会额外奖励一个商人单位和+1 [ICON_TradeRoute] 贸易路线容量。"		),
("en_US",	    "LOC_TRAIT_LEADER_ARTHASHASTRA_DESCRIPTION",                "Can declare a War of Territorial Expansion after gaining the Military Training Civic.  +2 [ICON_Movement] Movement and +5 [ICON_Strength] Combat Strength for the first 10 turns after declaring a War of Territorial Expansion. Receive a free builder when Encampment District is built, receive an extra free trader and +1 [ICON_TradeRoute] TradeRoute Capacity from the first Encampment District."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_STEPWELL_EXPANSION2_DESCRIPTION",	        "解锁建造者建造印度特色改良设施梯井的能力。[NEWLINE][NEWLINE]+1 [ICON_Food] 食物 +1 [ICON_Housing] 住房。每与一个圣地区相邻，则 +2 [ICON_Faith] 信仰值。每与一个农场相邻，则+1 [ICON_Food] 食物。干旱时防止 [ICON_Food] 食物损失。解锁“封建主义”和“殖民主义”后，将额外获得 [ICON_Faith] 信仰值和 [ICON_Production] 生产力。解锁“卫生”后，将额外+1 [ICON_Housing] 住房。不能建在另一个梯井旁。"		),
("en_US",	    "LOC_IMPROVEMENT_STEPWELL_EXPANSION2_DESCRIPTION",          "Unlocks the Builder ability to construct a Stepwell, unique to India.[NEWLINE][NEWLINE]+1 [ICON_Food] Food and +1 [ICON_Housing] Housing. +2 [ICON_Faith] Faith from each Holy Site District and +1 [ICON_Food] Food from each Farm it is adjacent to. Prevents [ICON_Food] Food loss during Drought. Provide additional [ICON_Faith] Faith and [ICON_Production] Production after unlocking 'Feudalism' and 'Colonialism', respectively. Additional +1 [ICON_Housing] Housing after unlocking 'Sanitation'. Cannot be built adjacent to another Stepwell."	),
("zh_Hans_CN",  "LOC_UNIT_INDIAN_VARU_DESCRIPTION",           "印度特色的古典时期重骑兵单位，克制远程单位。视野为2。[NEWLINE][NEWLINE]与其相邻的敌方单位 [ICON_Strength] 战斗力-5。"),
("en_US",       "LOC_UNIT_INDIAN_VARU_DESCRIPTION",           "Indian unique Classical era Heavy Cavalry unit, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE]Adjacent enemy units receive -5 [ICON_STRENGTH] Combat Strength."),
--CIVILIZATION_INDONESIA	
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_INDONESIA_NUSANTARA_DESCRIPTION",			"海岸和湖泊单元格可为所有专业化区域提供标准相邻加成。如与海岸或湖泊单元格相邻，娱乐中心+1 [ICON_AMENITIES] 宜居度，军营+1 [ICON_Housing] 住房。免费获得一份 [ICON_RESOURCE_SPICES] 香料、 [ICON_RESOURCE_CLOVES] 丁香和 [ICON_RESOURCE_CINNAMON] 肉桂。渔船和渔场+1 [ICON_PRODUCTION] 生产力。非沿海城市-1 [ICON_Amenities] 宜居度。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_INDONESIA_NUSANTARA_DESCRIPTION",			"Coast and Lake tiles provide standard adjacency bonus for all specialty districts.  +1 [ICON_AMENITIES] Amenity to each Entertainment Complex and +1 [ICON_Housing] housing to each Encampment if they are adjacent to a Coast or Lake tile. Receive one copy of [ICON_RESOURCE_SPICES] spices, [ICON_RESOURCE_CLOVES] cloves, and [ICON_RESOURCE_CINNAMON] cinnamon. +1 [ICON_PRODUCTION] production for Fishing boats and Fishery. -1 [ICON_Amenities] Amenity for non-coastal cities."		),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_EXALTED_GODDESS_DESCRIPTION",			"可用 [ICON_FAITH] 信仰值购买海军单位和平民单位。与海岸或湖泊单元格相邻的市中心+5 [ICON_FAITH] 信仰值+5 [ICON_GOLD] 金币。海岸单元格+2 [ICON_FAITH] 信仰值。"),
("en_US",	    "LOC_TRAIT_LEADER_EXALTED_GODDESS_DESCRIPTION",			"Naval units and Civilian units can be purchased with [ICON_FAITH] Faith. +5 [ICON_FAITH] Faith and +5 [ICON_GOLD] Gold to City Centers that are adjacent to Coast or Lake tiles. +2 [ICON_FAITH] Faith for coast tiles."		),
("zh_Hans_CN",	"LOC_IMPROVEMENT_KAMPUNG_DESCRIPTION",			"解锁建造者建造印度尼西亚特色改良设施“甘榜屋”的能力。[NEWLINE][NEWLINE]+1 [ICON_Food] 食物、+1 [ICON_PRODUCTION] 生产力、+1 [ICON_Housing] 住房。每个相邻渔船+1 [ICON_PRODUCTION] 生产力。解锁更多科技和市政后可获得更多 [ICON_PRODUCTION] 生产力、[ICON_Housing] 住房(需要科技“批量生产”)和 [ICON_TOURISM] 旅游业绩。[NEWLINE][NEWLINE]必须建造在靠近海洋资源的海岸或湖泊单元格上。"),
("en_US",	    "LOC_IMPROVEMENT_KAMPUNG_DESCRIPTION",			"Unlocks the Builder ability to construct a Kampung, unique to Indonesia.[NEWLINE][NEWLINE]+1 [ICON_Food] Food, +1 [ICON_PRODUCTION] Production and +1 [ICON_Housing] Housing. +1 [ICON_PRODUCTION] Production for each adjacent Fishing Boat. Additional [ICON_PRODUCTION] Production, [ICON_Housing] Housing (requires tech 'Mass production'), and  [ICON_TOURISM] Tourism as you advance through the Technology and Civics Tree. [NEWLINE][NEWLINE]Must be placed on a Coast or Lake tile adjacent to a sea resource."		),
--CIVILIZATION_JAPAN
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_ADJACENT_DISTRICTS_DESCRIPTION",			"当区域与区域相邻时，所有区域获得一个额外的标准相邻加成（但区域间的少量相邻加成和市中心的标准相邻加成无效）。宣布领土扩张战争后10回合内+100% [ICON_PRODUCTION] 生产力。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_ADJACENT_DISTRICTS_DESCRIPTION",			"All districts receive an additional standard adjacency bonus for being adjacent to another district (however, minor district adjacency bonus and city center standard adjacency bonus are disabled). +100% [ICON_PRODUCTION] production for the first 10 turns after declaring a War of Territorial Expansion."		),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_DIVINE_WIND_EXPANSION2_DESCRIPTION",			"陆地单位处于相邻海岸的陆地单元格时+5 [ICON_Strength] 战斗力；浅水单元格上的海军单位+5 [ICON_Strength] 战斗力。建造军营、圣地和剧院广场的时间减半。单位不受飓风伤害。如文明与日本交战时单位处于日本领土内，遭遇飓风时受到的伤害将+100%。海洋单元格+2 [ICON_Faith] 信仰值。"),
("en_US",	    "LOC_TRAIT_LEADER_DIVINE_WIND_EXPANSION2_DESCRIPTION",			"Land units receive +5 [ICON_Strength] Combat Strength in land tiles adjacent to Coast; naval units receive +5 [ICON_Strength] Combat Strength in shallow water tiles. Builds Encampment, Holy Site and Theater Square districts in half the time. Units do not receive damage from Hurricanes. Civilizations that are at war with Japan receive +100% unit damage from Hurricanes in Japanese territory. +2 [ICON_Faith] faith for ocean tiles."		),
("zh_Hans_CN",	"LOC_BUILDING_ELECTRONICS_FACTORY_EXPANSION2_DESCRIPTION",			"日本特色建筑。获得等于该区域相邻 [ICON_Production] 生产力加成的文化值 [ICON_Culture] 加成。[NEWLINE]其 [ICON_Production] 生产力加成延伸到4单元格内的所有市中心（前提是其尚未从其他的此建筑获得加成）。"),
("en_US",	    "LOC_BUILDING_ELECTRONICS_FACTORY_EXPANSION2_DESCRIPTION",			"A building unique to Japan. Bonus [ICON_Culture] Culture equal to the adjacency [ICON_Production] Production bonus of its district. [NEWLINE]Its [ICON_Production] Production bonus is extended to all City Centers within 4 tiles that do not already have a bonus from this building type. "		),
("zh_Hans_CN",	"LOC_GAEXP_BUILDING_ELECTRONICS_FACTORY_DESCRIPTION",			"日本特色建筑。获得等于该区域相邻 [ICON_Production] 生产力加成的文化值 [ICON_Culture] 加成。[NEWLINE]帝国境内的每处改良过且位于工业区两个单元格范围内的奢侈品资源为所有电子厂+1 [ICON_Production] 生产力。不会受到内卷化的影响。[NEWLINE][NEWLINE]其基础产出延伸到4个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"),
("en_US",	    "LOC_GAEXP_BUILDING_ELECTRONICS_FACTORY_DESCRIPTION",			"A building unique to Japan, Bonus [ICON_Culture] Culture equal to the adjacency [ICON_Production] Production bonus of its district.[NEWLINE]Each improved Luxury resource within 2 tiles of an Industrial Zone provides +1 [ICON_Production] Production for all Electronics Factories in the empire. No debuff from industry competition.[NEWLINE][NEWLINE]Its base bonus is extended to all City Centers within 4 tiles that do not already have a bonus from a Factory building."		),
("zh_Hans_CN",  "LOC_UNIT_JAPANESE_SAMURAI_DESCRIPTION",           "日本特色的中世纪近战单位，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]受伤时，不会遭受战斗减益。"),
("en_US",       "LOC_UNIT_JAPANESE_SAMURAI_DESCRIPTION",           "Japanese unique Medieval era Melee unit, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]Does not suffer combat penalties when damaged."),
--CIVILIZATION_KHMER	
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_KHMER_BARAYS_EXPANSION2_DESCRIPTION",			"水渠、堤坝、运河额外获得+2 [ICON_AMENITIES] 宜居度。农场与水渠、堤坝、或运河相邻时，额外+1 [ICON_FOOD] 食物+1 [ICON_Production] 生产力。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_KHMER_BARAYS_EXPANSION2_DESCRIPTION",			"Aqueduct, Dam, and Canal provide +2 additional [ICON_AMENITIES] Amenity. +1 [ICON_FOOD] Food and +1 [ICON_Production] Production for Farm if it is adjacent to Aqueduct, Canal, or Dam District. "		),	
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MONASTERIES_KING_EXPANSION2_DESCRIPTION",			"圣地可触发文化炸弹。圣地和圣地建筑为城市+1~+4 [ICON_HOUSING] 住房，并招揽+1~+4 [ICON_Citizen] 公民。祭祀建筑为城市提供+4所有类型产出。没有祭祀建筑的城市-10%所有类型产出。"),
("en_US",	    "LOC_TRAIT_LEADER_MONASTERIES_KING_EXPANSION2_DESCRIPTION",			"Holy Sites trigger a Culture Bomb. Holy Sites and its buildings provide +1~+4 [ICON_HOUSING] Housing and +1~+4 [ICON_Citizen] Citizens. Worship building provides +4 all types of yields. -10% all types of yields for cities without a worship building."		),	
("zh_Hans_CN",  "LOC_BUILDING_PRASAT_EXPANSION2_DESCRIPTION",           "+4 [ICON_FAITH] 信仰值。高棉特色建筑。替代寺庙。拥有此建筑后才能用 [ICON_Faith] 信仰值购买使徒和审判官。此城中每有1点 [ICON_CITIZEN] 人口，便+0.5 [ICON_Culture] 文化值。研究完“飞行”之后，如城市拥有10点或以上的 [ICON_CITIZEN] 人口，则+10 [ICON_Tourism] 旅游业绩；如城市拥有20点或以上的 [ICON_CITIZEN] 人口，则+20 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]其基础产出延伸到4个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"),
("en_US",       "LOC_BUILDING_PRASAT_EXPANSION2_DESCRIPTION",           "+4 [ICON_FAITH] Faith. A building unique to Khmer. Replaces the Temple. Required to purchase Apostles and Inquisitors with [ICON_Faith] Faith. +0.5 [ICON_Culture] Culture for every [ICON_CITIZEN] population in this city. Once Flight is researched receive +10 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 10 or higher and +20 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 20 or higher.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 4 tiles that do not already have a bonus from a Holy Site Tier 2 building."),
("zh_Hans_CN",  "LOC_BUILDING_PRASAT_HEROES_XP2_DESCRIPTION",           "+4 [ICON_FAITH] 信仰值。高棉特色建筑。替代寺庙。拥有此建筑后才能用 [ICON_Faith] 信仰值购买使徒和审判官。此城中每有1点 [ICON_CITIZEN] 人口，便+0.5 [ICON_Culture] 文化值。研究完“飞行”之后，如城市拥有10点或以上的 [ICON_CITIZEN] 人口，则+10 [ICON_Tourism] 旅游业绩；如城市拥有20点或以上的 [ICON_CITIZEN] 人口，则+20 [ICON_Tourism] 旅游业绩。在此城中召回英雄所花费的 [ICON_Faith] 信仰值-15%。[NEWLINE][NEWLINE]其基础产出延伸到4个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"),
("en_US",       "LOC_BUILDING_PRASAT_HEROES_XP2_DESCRIPTION",           "+4 [ICON_FAITH] Faith. A building unique to Khmer. Replaces the Temple. Required to purchase Apostles and Inquisitors with [ICON_Faith] Faith. +0.5 [ICON_Culture] Culture for every [ICON_CITIZEN] population in this city. Once Flight is researched receive +10 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 10 or higher and +20 [ICON_Tourism] Tourism if the city [ICON_CITIZEN] population is 20 or higher. Recalling Heroes in this city costs 15% less [ICON_Faith] Faith.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 4 tiles that do not already have a bonus from a Holy Site Tier 2 building."),
("zh_Hans_CN",  "LOC_UNIT_KHMER_DOMREY_DESCRIPTION",           "高棉特色中世纪攻城单位。视野为1。[NEWLINE][NEWLINE]可形成控制区。"),
("en_US",       "LOC_UNIT_KHMER_DOMREY_DESCRIPTION",           "Khmer unique Medieval era Siege unit. Sight: 1[NEWLINE][NEWLINE]Can exerts zone of control."),
--CIVILIZATION_KONGO
("zh_Hans_CN",	"LOC_TRAIT_LEADER_RELIGIOUS_CONVERT_DESCRIPTION",			"无法建造圣地区、获得大预言家，或创建宗教。从已成为多数刚果城市信仰的宗教中获得创始人信仰信条加成。信仰宗教的城市忠诚度固定为100。每当其他文明招募大预言家时，随机获得3个 [ICON_TechBoosted] 尤里卡和 [ICON_CivicBoosted] 鼓舞。森林和雨林+2 [ICON_Faith] 信仰。"),
("en_US",	    "LOC_TRAIT_LEADER_RELIGIOUS_CONVERT_DESCRIPTION",			"May not build Holy Site districts, gain Great Prophets, or found Religions. Gains all Beliefs of any Religion that has established itself in a majority of his cities. Cities with a religion have 100% loyalty. Receive 3 random [ICON_TechBoosted] Eureka and [ICON_CivicBoosted] Inspiration each time another civilization recruits a Great Prophet. +2 [ICON_Faith] Faith for Forest and Jungle tiles. "		),	
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_NKISI_DESCRIPTION",			        "每个 [ICON_GreatWork_WRITING] 著作、[ICON_GreatWork_Landscape] 艺术、以及 [ICON_GreatWork_MUSIC] 音乐巨作获得+2所有类型产出，且这些巨作产出的 [ICON_Tourism] 旅游业绩+100%。获得额外50% [ICON_GreatWriter] 大作家、[ICON_GreatArtist] 大艺术家、[ICON_GreatMusician] 大音乐家点数。宫殿有5个巨作槽位。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_NKISI_DESCRIPTION",			        "+2 all types of yields and +100% [ICON_Tourism] tourism for each [ICON_GreatWork_WRITING] Writing, [ICON_GreatWork_Landscape] Art, and [ICON_GreatWork_MUSIC] Music Great Work in addition to the usual [ICON_Culture] Culture. Receive 50% more [ICON_GreatWriter] Great Writer, [ICON_GreatArtist] Great Artist, [ICON_GreatMusician] Great Musician points. Palace has slots for 5 Great Works."		),	
("zh_Hans_CN",	"LOC_DISTRICT_MBANZA_DESCRIPTION",			                "刚果特色区域，只能建在雨林或树林里。代替社区，可更早建造。无视魅力值固定提供+6 [ICON_Housing] 住房。城市人口增长速度10%。+2所有产出。"),
("en_US",	    "LOC_DISTRICT_MBANZA_DESCRIPTION",			                "A district unique to Kongo that can only be constructed in Rainforest or Woods. Replaces the Neighborhood district but is available earlier and cheaper to build. City growth rate increase 25%. Provides +6 [ICON_Housing] Housing regardless of Appeal. +2 for all types of yields."		),	
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_NKISI_HEROES_DESCRIPTION",			"每个 [ICON_GreatWork_WRITING] 著作、[ICON_GreatWork_Landscape] 艺术、以及 [ICON_GreatWork_MUSIC] 音乐巨作获得+2所有类型产出，且这些巨作产出的 [ICON_Tourism] 旅游业绩+100%。获得额外50% [ICON_GreatWriter] 大作家、[ICON_GreatArtist] 大艺术家、[ICON_GreatMusician] 大音乐家点数。宫殿有5个巨作槽位。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_NKISI_HEROES_DESCRIPTION",			"+2 all types of yields and +100% [ICON_Tourism] tourism for each [ICON_GreatWork_WRITING] Writing, [ICON_GreatWork_Landscape] Art, and [ICON_GreatWork_MUSIC] Music Great Work in addition to the usual [ICON_Culture] Culture. Receive 50% more [ICON_GreatWriter] Great Writer, [ICON_GreatArtist] Great Artist, [ICON_GreatMusician] Great Musician points. Palace has slots for 5 Great Works."		),	
("zh_Hans_CN",  "LOC_UNIT_KONGO_SHIELD_BEARER_DESCRIPTION",           "刚果特色的古典时期近战单位，替代剑客，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]防御远程攻击时+10 [ICON_Strength] 战斗力。能穿行以及视野能穿过树林和雨林。"),
("en_US",       "LOC_UNIT_KONGO_SHIELD_BEARER_DESCRIPTION",           "Kongo unique Classical era Melee unit that replaces the Swordsman, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength when defending against ranged attacks. Can move and see through Woods and Rainforest."),
--CIVILIZATION_KOREA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_THREE_KINGDOMS_DESCRIPTION",			"农场与其他农场，渔场与其他渔场相邻时，额外产出+1 [ICON_FOOD] 食物。矿山与其他矿山，伐木场与其他伐木场相邻时，额外产出+1 [ICON_Production] 生产力。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_THREE_KINGDOMS_DESCRIPTION",			"+1 [ICON_FOOD] Food for farm if it is adjacent to other farm. +1 [ICON_FOOD] Food for fishery if it is adjacent to other fishery. +1 [ICON_Production] Production for mine if it is adjacent to other mine. +1 [ICON_Production] Production for lumber mill if it is adjacent to other lumber mill."		),	
("zh_Hans_CN",	"LOC_TRAIT_LEADER_HWARANG_DESCRIPTION",			                "[ICON_Governor] 总督在城市中就职后，每次升级（包括首次升级）都将提供+5% 的所有类型产出。但没有 [ICON_Governor] 总督就职的城市-10%所有类型产出。"),
("en_US",	    "LOC_TRAIT_LEADER_HWARANG_DESCRIPTION",			                "[ICON_Governor] Governors established in a city provide +5% for all types of yields for each Promotion they have earned, including their first. But cities without established [ICON_Governor] Governors -10% for all types of yields."		),	
("zh_Hans_CN",	"LOC_DISTRICT_SEOWON_DESCRIPTION",			                "朝鲜特色科技区域。替代学院区域。[NEWLINE][NEWLINE]与学院的区别在于，自身产出+3 [ICON_SCIENCE] 科技值，解锁“教育”和“天文学”科技之后分别再+3 [ICON_SCIENCE] 科技值。但与其他区域相邻时-1 [ICON_SCIENCE] 科技值，且地形和地貌不提供相邻加成。更多的专家 [ICON_SCIENCE] 科技值产出。必须建造在丘陵上。"),
("en_US",	    "LOC_DISTRICT_SEOWON_DESCRIPTION",			                "A district unique to Korea for scientific endeavors. Replaces the Campus district. [NEWLINE][NEWLINE]+3 [ICON_SCIENCE] Science, extra +3 [ICON_SCIENCE] Science after unlocking 'Tech Education' and 'Tech Astronomy'. But -1 [ICON_SCIENCE] Science from each district it is adjacent to, and no district adjacency bonus. More specialist [ICON_SCIENCE] Science yield. Must be built on Hills."		),	
("zh_Hans_CN",  "LOC_UNIT_KOREAN_HWACHA_DESCRIPTION",           "朝鲜特色的文艺复兴时代远程单位，替代野战加农炮，克制近战单位。视野为1。[NEWLINE][NEWLINE][ICON_Ranged] 远程攻击力强。在一回合中移动后无法进行攻击。"),
("en_US",       "LOC_UNIT_KOREAN_HWACHA_DESCRIPTION",           "Korean unique Renaissance Era Ranged unit that replaces the Field Cannon, counters Melee units well. Sight: 1 [NEWLINE][NEWLINE]High [ICON_Ranged] Ranged Attack Strength. Cannot move and attack in the same turn."),
--CIVILIZATION_MACEDON
("zh_Hans_CN",	"LOC_TRAIT_LEADER_HELLENISTIC_FUSION_EXPANSION1_DESCRIPTION",		"征服非自由城市的城市后，其中每个军营区、工业区或学院区将启动1个 [ICON_TechBoosted] 尤里卡、每个圣地区、商业区或剧院广场则将带来1次 [ICON_CivicBoosted] 鼓舞。[ICON_Governor] 总督就职城市9单元格范围内的所有城市每回合对本方文明的忠诚度+4。"		),
("en_US",	    "LOC_TRAIT_LEADER_HELLENISTIC_FUSION_EXPANSION1_DESCRIPTION",		"When conquering a city that is not a Free City receive a [ICON_TechBoosted] Eureka for each Encampment, Industrial zone, or Campus in the conquered city and an [ICON_CivicBoosted] Inspiration for each Holy Site, Commercial hub, or Theater Square. All cities within 9 tiles of a city with your  [ICON_Governor] Governor gain +4 Loyalty per turn towards your civilization. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_TO_WORLDS_END_DESCRIPTION",		"城市不会产生厌战情绪。此玩家占领拥有世界奇观的城市时，所有军事单位的体力值将完全恢复。你的所有单位均无视区域控制，且若于平坦的单元格开始一个回合，则+1 [ICON_Movement] 移动力。单位发起攻击时获得的经验值+100%。"		),
("en_US",	    "LOC_TRAIT_LEADER_TO_WORLDS_END_DESCRIPTION",		"Cities do not incur war weariness. All military units heal completely when this player captures a city with a world wonder. Your units can ignore Zone of control, and gain +1 [ICON_Movement] Movement when starting a turn on a clear terrain. +100% experience for combat units when attacking."	),
("zh_Hans_CN",	"LOC_BUILDING_BASILIKOI_PAIDES_EXPANSION2_DESCRIPTION",		"马其顿特色建筑。该城中训练的陆地近战、远程单位和伙友骑兵+25%战斗经验值。非平民单位在此城中创建时，获得等同于单位花费25%的 [ICON_SCIENCE] 科技值。专家获得+1 [ICON_SCIENCE] 科技值。[NEWLINE][NEWLINE]战略资源储备+10（标准速度下）。[NEWLINE][NEWLINE]无法建造在已拥有马厩的军营区域中。"		),
("en_US",	    "LOC_BUILDING_BASILIKOI_PAIDES_EXPANSION2_DESCRIPTION",		"A building unique to Macedon.+25% combat experience for all melee, ranged land units, and Hetairoi trained in this city. Gain [ICON_SCIENCE] Science equal to 25% of the unit's cost when a non civilian unit is created in this city. +1 [ICON_SCIENCE] Science for specialists.[NEWLINE][NEWLINE]Strategic Resource Stockpiles increased +10 (on Standard Speed).[NEWLINE][NEWLINE]May not be built in an Encampment district that already has a Stable."	),
("zh_Hans_CN",	"LOC_ABILITY_IGNORE_ZOC_MOVEMENT_DESCRIPTION",		"无视区域控制，若于平坦单元格上开始一个回合，则+1 [ICON_Movement] 移动力。"		),
("en_US",	    "LOC_ABILITY_IGNORE_ZOC_MOVEMENT_DESCRIPTION",		"Ignore ZOC. +1 [ICON_Movement] movement if begin a new turn at a clear terrain."	),
("zh_Hans_CN",  "LOC_UNIT_MACEDONIAN_HYPASPIST_DESCRIPTION",           "马其顿特色的古典时期近战单位，替代剑客，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]围攻区域时+5 [ICON_Strength] 战斗力。拥有50%的额外支援加成。"),
("en_US",       "LOC_UNIT_MACEDONIAN_HYPASPIST_DESCRIPTION",           "Macedonian unique Classical era Melee unit that replaces the Swordsman, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+5 [ICON_Strength] Combat Strength when besieging districts. 50% Additional Support Bonus."),
("zh_Hans_CN",  "LOC_UNIT_MACEDONIAN_HETAIROI_DESCRIPTION",           "马其顿特色的古典时期重骑兵单位，替代骑手，克制远程单位。视野为2。[NEWLINE][NEWLINE]与大将军相邻时额外+5 [ICON_Strength] 战斗力。击杀敌方单位时+5大将军点数。初始便拥有1次免费升级。"),
("en_US",       "LOC_UNIT_MACEDONIAN_HETAIROI_DESCRIPTION",           "Macedonian unique Classical era Heavy Cavalry unit that replaces the Horseman, counters Ranged units well. Sight: 2 [NEWLINE][NEWLINE]Additional +5 [ICON_Strength] Combat Strength when adjacent to a Great General. +5 Great General points when killing an enemy unit. Starts with 1 free Promotion."),
--CIVILIZATION_MALI
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MALI_GOLD_DESERT_DESCRIPTION",		"每个相邻沙漠或沙漠丘陵单元格为市中心提供+1 [ICON_FOOD] 食物，+1 [ICON_PRODUCTION] 生产力，+1 [ICON_FAITH] 信仰值，+1 [ICON_GOLD] 金币。矿山-1 [ICON_PRODUCTION] 生产力，但+4 [ICON_GOLD] 金币。建造建筑或生产单位时-30% [ICON_PRODUCTION] 生产力。单位不会受到沙尘暴的伤害。与马里交战的文明单位如处于马里领土中，遭遇沙尘暴时受到的伤害+100%。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MALI_GOLD_DESERT_DESCRIPTION",		"City Centers gain +1 [ICON_FOOD], +1 [ICON_Production] Production, +1 [ICON_GOLD] Gold, +1 [ICON_FAITH] Faith for every adjacent Desert and Desert Hills tiles. Mines receive -1 [ICON_PRODUCTION] Production and +4 [ICON_GOLD] Gold. -30% [ICON_PRODUCTION] Production toward constructing buildings or training units. Units do not receive damage from Dust storm. Civilizations that are at war with Mali receive +100% unit damage from Dust storm in Mali's territory. "	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_SAHEL_MERCHANTS_DESCRIPTION",		        "起始城市中的每个沙漠地形的单元格为国际 [ICON_TradeRoute] 贸易路线提供+1 [ICON_GOLD] 金币。文明进入黄金时代后可获得+2 [ICON_TradeRoute] 贸易路线容量。每完成一条贸易路线+1时代得分。使用 [ICON_GOLD] 金币和 [ICON_FAITH] 信仰值在此城中购买单位、建筑和区域时，费用减少20%。"		),
("en_US",	    "LOC_TRAIT_LEADER_SAHEL_MERCHANTS_DESCRIPTION",		        "International [ICON_TradeRoute] Trade Routes gain +1 [ICON_GOLD] Gold for every Desert tile in the origin city. Receive +2 [ICON_TradeRoute] Trade Capacity every time you enter a Golden Age. Gain +1 Era Score each time you successfully complete a [ICON_TradeRoute] Trade Route. Units, Buildings, and Districts are 20% cheaper to purchase with [ICON_GOLD] Gold and [ICON_FAITH] Faith in this City."	),
("zh_Hans_CN",	"LOC_DISTRICT_SUGUBA_DESCRIPTION",		        "马里特色区域，专精于经济与贸易，取代商业中心。[NEWLINE][NEWLINE]与商业中心的不同在于，从每个相邻资源获得超大量 [ICON_Gold] 金币加成，但不从港口和运河获得相邻加成。"		),
("en_US",	    "LOC_DISTRICT_SUGUBA_DESCRIPTION",		        "A district unique to Mali specializing in finance and trade that replaces the Commercial Hub. [NEWLINE][NEWLINE]Gain tremendous [ICON_Gold] Gold bonus for each adjacent resource. "	),
("zh_Hans_CN",  "LOC_UNIT_MALI_MANDEKALU_CAVALRY_DESCRIPTION",           "马里特色的中世纪重骑兵单位，替代骑士，克制远程单位。视野为2。[NEWLINE][NEWLINE]位于陆地且与曼德卡鲁骑兵相距4个单元格内的商人单位可免遭掠夺。战斗胜利可获得等同于该单位基础 [ICON_Strength] 战斗力数值的 [ICON_GOLD] 金币。"),
("en_US",       "LOC_UNIT_MALI_MANDEKALU_CAVALRY_DESCRIPTION",           "Mali unique Medieval Era Heavy Cavalry unit unit that replaces the Knight, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE]Trader units are immune to being plundered if they are within 4 tiles of a Mandekalu Cavalry and on a land tile. Combat victories provide [ICON_GOLD] Gold equal to 100% that unit's base [ICON_Strength] Combat Strength."),
--CIVILIZATION_MAORI
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MAORI_MANA_DESCRIPTION",		"游戏初始便解锁“航海术”与“造船术”科技，同时拥有进入海洋单元格的能力。水运单位+2 [ICON_Movement] 移动力。未改良的树林与雨林提供+1 [ICON_PRODUCTION] 生产力。“重商主义”额外提供+1 [ICON_PRODUCTION] 生产力、“保护地球”额外+2 [ICON_PRODUCTION] 生产力。无法收获资源；无法获得 [ICON_GreatWriter] 大作家。每个相邻海岸单元格为市中心提供+1所有类型产出。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MAORI_MANA_DESCRIPTION",		"Begin the game with the Sailing and Shipbuilding technologies unlocked and with the ability to enter Ocean tiles. Embarked units gain +2 [ICON_Movement] Movement. Unimproved Woods and Rainforest are +1 [ICON_PRODUCTION] Production. Additional +1 [ICON_PRODUCTION] Production from Mercantilism and +2 [ICON_PRODUCTION] Production from Conservation. Resources cannot be harvested. [ICON_GreatWriter] Great Writers cannot be earned. City Centers gain +1 for all types of yields for every adjacent Coast tiles."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_KUPES_VOYAGE_DESCRIPTION",		    "游戏开始时位于海洋单元格中。免费获得1名建造者，建立首座城市时+2 [ICON_Citizen] 人口。宫殿获得+3 [ICON_HOUSING] 住房与+1 [ICON_AMENITIES] 宜居度。建立首座城市前每回合获得+2 [ICON_SCIENCE] 科技值与+2 [ICON_CULTURE] 文化值。"		),
("en_US",	    "LOC_TRAIT_LEADER_KUPES_VOYAGE_DESCRIPTION",		    "Begin the game in an Ocean tile. Gain a free Builder and +2 [ICON_Citizen] Population when settling your first city. The Palace receives +3 [ICON_HOUSING] Housing and +1 [ICON_AMENITIES] Amenity. +2 [ICON_SCIENCE] Science and +2 [ICON_CULTURE] Culture per turn before you settle your first city."	),
-- ("zh_Hans_CN",	"LOC_BUILDING_MARAE_DESCRIPTION",		                "毛利特色建筑。城市中所有包含可通行地貌或自然奇观的单元格+1 [ICON_CULTURE] 文化值与 [ICON_FAITH] 信仰值。研究“飞行”科技后，城市中所有包含地貌或自然奇观的单元格+1 [ICON_TOURISM] 旅游业绩。维护免费；无巨作槽位。[NEWLINE][NEWLINE]其基础产出延伸到3个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"		),
-- ("en_US",	    "LOC_BUILDING_MARAE_DESCRIPTION",		                "A building unique to the Māori. +1 [ICON_CULTURE] Culture and [ICON_FAITH] Faith to all of this city's tiles with a passable feature or natural wonder. After Flight is researched receive +1 [ICON_TOURISM] Tourism to all of this city's tiles with a feature or natural wonder. Costs no maintenance. Has no Great Work slots.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 3 tiles that do not already have a bonus from a Theater Tier 1 building."	),
("zh_Hans_CN",  "LOC_UNIT_MAORI_TOA_DESCRIPTION",           "毛利特色的古典时期近战单位，替代剑客，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]与其相邻的敌方单位 [ICON_Strength] 战斗力-5。能够建造特色改良设施“帕堡”。"),
("en_US",       "LOC_UNIT_MAORI_TOA_DESCRIPTION",           "Māori unique Classical era Melee unit, counters Anti-Cavalry units well. Sight: 1 [NEWLINE][NEWLINE]Adjacent enemy units receive -5 [ICON_STRENGTH] Combat Strength. Can build the unique Pā improvement."),
--CIVILIZATION_MAPUCHE
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MAPUCHE_TOQUI_DESCRIPTION",		"[ICON_Governor] 总督就职城市提供的 [ICON_CULTURE] 文化值+5%、[ICON_Production] 生产力+5%，此城市中生产的所有单位的战斗经验值+10%。在非马普切建立的城市中，这些数值将乘以三倍。丘陵地形为你的所有专业化区域提供标准的相邻加成，但每两个相同的平坦地形为你的所有专业化区域-1对应相邻加成。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MAPUCHE_TOQUI_DESCRIPTION",		"Cities with an Established [ICON_Governor] Governor provide +5% [ICON_CULTURE] Culture, +5% [ICON_Production] Production, and +10% experience in combat towards all units trained in this city. These numbers are tripled in cities not founded by the Mapuche. Specialty districts gain a Standard adjacency bonus from Hills terrains, however, -1 corresponding adjacency bonus from each two adjacent flat terrains of the same type."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_LAUTARO_ABILITY_DESCRIPTION_ALT",		"你的陆地战斗单位在丘陵中移动无 [ICON_Movement] 移动力惩罚，且不易被敌人发现。与自由城市或处在黄金/英雄时代中的文明作战时+10 [ICON_Strength] 战斗力。在敌方城市边界中击杀敌方单位后，将使此城损失20点忠诚度。在敌方城市掠夺格位将使该城损失5点忠诚度。"		),
("en_US",	    "LOC_TRAIT_LEADER_LAUTARO_ABILITY_DESCRIPTION_ALT",		"Your land combat units have faster [ICON_Movement] Movement on Hill terrain and are invisible until they are closed to enemies. +10 [ICON_Strength] Combat Strength when fighting Free Cities or civilizations that are in a Golden or Heroic Age. Defeating an enemy unit within the borders of an enemy city causes that city to lose 20 loyalty. Pillaging a plot in an enemy city causes it to lose 5 loyalty."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_CHEMAMULL_DESCRIPTION",		        "解锁建造者建造马普切木人（马普切特色改良设施）的能力。[NEWLINE][NEWLINE]+1 [Icon_Production] 生产力。提供所在单元格魅力100%的 [ICON_CULTURE] 文化值。研究完“飞行”后可获得更多 [ICON_TOURISM] 旅游业绩。[NEWLINE][NEWLINE]要求单元格至少达到迷人的魅力。"		),
("en_US",	    "LOC_IMPROVEMENT_CHEMAMULL_DESCRIPTION",		        "Unlocks the Builder ability to construct a Chemamull, unique to Mapuche.[NEWLINE][NEWLINE]+1 [Icon_Production] Production. Provides [ICON_CULTURE] Culture equal to 100% of the tile's Appeal. Additional [ICON_TOURISM] Tourism after researching Flight. [NEWLINE][NEWLINE]Minimum Appeal of Charming."	),
("zh_Hans_CN",	"LOC_ABILITY_IGNORE_HILL_INVISIBLE_DESCRIPTION",		"丘陵中移动无 [ICON_Movement] 移动力惩罚且视野可以穿过丘陵，且不易被敌人发现。"		),
("en_US",	    "LOC_ABILITY_IGNORE_HILL_INVISIBLE_DESCRIPTION",		"It has faster [ICON_Movement] Movement on Hill terrain and is invisible until it is closed to enemies. "	),
("zh_Hans_CN",  "LOC_UNIT_MAPUCHE_MALON_RAIDER_DESCRIPTION",           "马普切特色的文艺复兴时代轻骑兵单位，克制攻城单位。视野为2。[NEWLINE][NEWLINE]在友方领土4个单元格内时+5 [ICON_Strength] 战斗力。可消耗1点 [ICON_Movement] 移动力进行掠夺。"),
("en_US",       "LOC_UNIT_MAPUCHE_MALON_RAIDER_DESCRIPTION",           "Mapuche unique Renaissance Era Light Cavalry unit, counters Siege units well. Sight: 2 [NEWLINE][NEWLINE]+5 [ICON_Strength] Combat Strength bonus if within 4 hexes of friendly territory. Pillaging costs 1 [ICON_Movement] Movement."),
--CIVILIZATION_MAYA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MAYAB_DESCRIPTION",		"在淡水和海岸旁定居时不提供额外 [ICON_HOUSING] 住房，而每座农场和种植园额外+1 [ICON_HOUSING] 住房和+1 [ICON_GOLD] 金币。每有一种奢侈品与市中心相邻，则+1 [ICON_AMENITIES] 宜居度。 "		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MAYAB_DESCRIPTION",		"Settling adjacent to Fresh Water and Coast do not provide extra [ICON_HOUSING] Housing, instead each Farms and Plantations provide an additional +1 [ICON_HOUSING] Housing and +1 [ICON_GOLD] Gold. +1 [ICON_AMENITIES] Amenity for every Luxury adjacent to the City Center."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_MUTAL_DESCRIPTION",		"[ICON_Capital] 首都6个单元格范围内的非首都城市所有产出+15%，且建成时将获得1名建造者。其他城市所有产出-10%。[ICON_Capital] 首都6个单元格范围内的单位+5 [ICON_Strength] 战斗力。"		),
("en_US",	    "LOC_TRAIT_LEADER_MUTAL_DESCRIPTION",		"Non capital cities within 6 tiles of the [ICON_Capital] Capital gain +15% to all yields and when founded receive a builder. Other non capital cities receive -10% to all yields. +5 [ICON_Strength] Combat Strength to units within 6 tiles of the [ICON_Capital] Capital."	),
("zh_Hans_CN",	"LOC_DISTRICT_OBSERVATORY_DESCRIPTION",		"玛雅特色科学研究区域。替代“学院”区域，且建造花费更低。[NEWLINE][NEWLINE]与学院的区别在于，与山脉相邻时获得大量 [ICON_Science] 科技值相邻加成，但不从其他地形和地貌获得相邻加成。产出更多的 [ICON_GreatScientist] 大科学家点数。"		),
("en_US",	    "LOC_DISTRICT_OBSERVATORY_DESCRIPTION",		"A district unique to the Maya for scientific endeavors. Replaces the Campus district and cheaper to build.[NEWLINE][NEWLINE]Gain tremendous [ICON_Science] science adjacency bonus when it is adjacent to mountains. Provide more [ICON_GreatScientist] GreatScientist point."	),
("zh_Hans_CN",  "LOC_UNIT_MAYAN_HULCHE_DESCRIPTION",           "玛雅特色的远古时代远程单位，替代弓箭手，克制近战单位。视野为1。[NEWLINE][NEWLINE]远程攻击力强。与受伤的对手作战时+5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_MAYAN_HULCHE_DESCRIPTION",           "Mayan unique Ancient Era Ranged unit that replaces the Archer, counters Melee units well. Sight: 1[NEWLINE][NEWLINE]Strong ranged attack. +5 [ICON_Strength] Combat Strength when fighting a wounded opponent."),
--CIVILIZATION_MONGOLIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MONGOLIAN_ORTOO_DESCRIPTION",		            "创建 [ICON_TradeRoute] 贸易路线后，目的地城市中将立即出现1座 [ICON_TradingPost] 贸易站。在一个文明的任意城市中拥有1座 [ICON_TradingPost] 贸易站后，[ICON_VisLimited] 外交能见度则上升1级。您的 [ICON_TradeRoute] 贸易路线经过您的城市贸易站+1 [ICON_Production] 生产力。所有骑兵类单位+7 [ICON_Strength] 战斗力且可在攻击后移动。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MONGOLIAN_ORTOO_DESCRIPTION",		            "Starting a [ICON_TradeRoute] Trade Route immediately creates a [ICON_TradingPost] Trading Post in the destination city. Receive an extra level of [ICON_VisLimited] Diplomatic Visibility for possessing a [ICON_TradingPost] Trading Post in any city of a civilization. Your [ICON_TradeRoute] Trade Routes earn +1 [ICON_Production] Production for passing through Trading Posts in your own cities. All cavalry class units gain +7 [ICON_Strength] Combat Strength and can move after attacking."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GENGHIS_KHAN_ABILITY_DESCRIPTION",		            "解锁前置科技后立即获得“骑马”和“马镫”科技。所有骑兵类单位击败敌方骑兵类单位后有几率将其俘获。占领的城市每有两个专业化区域，所有单位便恢复30点体力值。当前政体的一个经济政策槽位转变为军事政策槽位。"		),
("en_US",	    "LOC_TRAIT_LEADER_GENGHIS_KHAN_ABILITY_DESCRIPTION",		            "After researching the pre-tech, receive tech 'Horseback riding' and 'Stirrups' immediately. All cavalry class units have a chance to capture defeated enemy cavalry class units. All units heal 30 health for each two Specialty districts in the city you captured. One economic policy slot is converted into military policy slot. "	),
("zh_Hans_CN",	"LOC_ABILITY_GENGHIS_KHAN_CAVALRY_MOVE_AFTER_ATTACK_DESCRIPTION",		"若移动力允许，可以在进攻之后移动。（驿站）"		),
("en_US",	    "LOC_ABILITY_GENGHIS_KHAN_CAVALRY_MOVE_AFTER_ATTACK_DESCRIPTION",		"Can move after attacking if there is movement left. (Ortoo)"	),
("zh_Hans_CN",	"LOC_ABILITY_GENGHIS_KHAN_CAVALRY_BONUS_DESCRIPTION",		"+7 [ICON_Strength] 战斗力。（驿站）"		),
("en_US",	    "LOC_ABILITY_GENGHIS_KHAN_CAVALRY_BONUS_DESCRIPTION",		"+7 [ICON_Strength] Combat Strength. (Ortoo)"	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GENGHIS_KHAN_ABILITY_NAME",		        "一代天骄"		),
("zh_Hans_CN",	"LOC_ABILITY_ORDU_TRAINED_UNIT_XP_DESCRIPTION",		        "+50%战斗经验值（斡耳朵）"		),
("en_US",	    "LOC_ABILITY_ORDU_TRAINED_UNIT_XP_DESCRIPTION",		        "+50% combat experience from Ordu"	),
("zh_Hans_CN",	"LOC_BUILDING_ORDU_DESCRIPTION",		                    "蒙古特色建筑。其所在城市中生产的轻重骑兵单位 [ICON_MOVEMENT] 移动力+1。其所在城市中生产的所有骑兵类单位战斗经验值+50%。无法建造在已拥有兵营的军营区域中。"		),
("en_US",	    "LOC_BUILDING_ORDU_DESCRIPTION",		                    "A building unique to Mongolia. Grants an ability that gives +1 [ICON_MOVEMENT] Movement to Heavy and Light Cavalry trained in this city. +50% combat experience for all cavalry class units trained in this city. May not be built in an Encampment district that already has a Barracks."	),
("zh_Hans_CN",  "LOC_UNIT_MONGOLIAN_KESHIG_DESCRIPTION",           "蒙古特色的中世纪火力支援单位，克制近战单位。视野为2。[NEWLINE][NEWLINE]可利用自身的高 [ICON_Movement] 移动力护送平民和支援单位。"),
("en_US",       "LOC_UNIT_MONGOLIAN_KESHIG_DESCRIPTION",           "Mongolian unique Medieval Era Firesupport unit, counters Melee units well. Sight: 2 [NEWLINE][NEWLINE] Can escort moving civilian and support units at their higher [ICON_Movement] Movement speed."),
--CIVILIZATION_NETHERLANDS
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GROTE_RIVIEREN_EXPANSION2_DESCRIPTION",		"与河流相邻时，所有专业化区域将获得大量加成。草原泛滥平原和平原泛滥平原+2魅力。与水渠，运河，堤坝区域相邻的单元格的地形产出翻倍。公民无法在山脉上工作。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GROTE_RIVIEREN_EXPANSION2_DESCRIPTION",		"Major adjacency bonus for specialty districts if next to a river. +2 appeal for grassland floodplains and plain floodplains. Double base terrain yields for tiles that are adjacent to Aqueduct, Canal, or Dam districts. Citizens cannot work on mountains tiles."	),
("zh_Hans_CN",	"LOC_TRAIT_RADIO_ORANJE_DESCRIPTION",		"通往外国城市或起源于外国城市的 [ICON_TradeRoute] 贸易路线+2 [ICON_Culture] 文化值+2 [ICON_Gold] 金币。若其在过去10回合内遭到宣战，则+50% [ICON_CULTURE] 文化值和 [ICON_GOLD] 金币。与主流文明处于战争状态时，所有城市的忠诚度固定为100。科技和市政达到文艺复兴时期后，政体+2外交政策槽位。"		),
("en_US",	    "LOC_TRAIT_RADIO_ORANJE_DESCRIPTION",		"[ICON_TradeRoute] Trade routes to foreign cities or from foreign cities provide +2 [ICON_Culture] Culture +2 [ICON_Gold] Gold to you. +50% [ICON_CULTURE] Culture and [ICON_GOLD] gold if you have received a declaration of war in the past 10 turns. All your cities are always 100% loyal when you are at war with a major civilization. +2 diplomatic slots for your government when your tech and civic reach Renaissance era."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_POLDER_DESCRIPTION",		"解锁建造者建造荷兰特色改良设施“圩田”的能力。[NEWLINE][NEWLINE]+1 [ICON_FOOD] 食物、+1 [ICON_Production] 生产力、+1 [ICON_Gold] 金币、+2 [ICON_Housing] 住房。单元格的 [ICON_Movement] 移动力消耗增至3点。[NEWLINE]解锁土木工程“土木工程”和“化学”后，如与其它圩田改良设施相邻，即可获得更多 [ICON_FOOD] 食物、[ICON_PRODUCTION] 生产力和 [ICON_GOLD] 金币。[NEWLINE][NEWLINE]必须建在与3个或更多可通行陆地单元格相邻的海岸或湖泊单元格上。"		),
("en_US",	    "LOC_IMPROVEMENT_POLDER_DESCRIPTION",		"Unlocks the Builder ability to construct a Polder, unique to Netherlands.[NEWLINE][NEWLINE]+1 [ICON_FOOD] Food, +1 [ICON_Production] Production,+1 [ICON_Gold] Gold, and +2 [ICON_Housing] Housing. Increases [ICON_Movement] Movement Cost of tile to 3.[NEWLINE]Additional [ICON_FOOD] Food, [ICON_PRODUCTION] Production and  [ICON_GOLD] Gold if adjacent to a Polder improvement as you advance through the Civics and Technology Tree. [NEWLINE][NEWLINE]Must be placed on a Coast or Lake tile adjacent to 3 or more passable land tiles. "	),
--CIVILIZATION_NORWAY
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_EARLY_OCEAN_NAVIGATION_DESCRIPTION",		"在研究造船术后，单位获得进入海洋单元格的能力。海军近战单位可在中立领土上回复。单位忽略来自上船和下船的额外 [ICON_Movement] 移动力消耗。在海岸边新建城市时获得免费的海军近战单位。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_EARLY_OCEAN_NAVIGATION_DESCRIPTION",		"Units gain the ability to enter Ocean tiles after researching the Shipbuilding technology. Naval melee units heal in neutral territory. Units ignore additional [ICON_Movement] Movement costs from embarking and disembarking. Receive a free naval melee unit when you build a coastal city."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_THUNDERBOLT_EXPANSION2_DESCRIPTION",		        "使所有海军近战单位均能发动海岸突袭，生产所有海军近战单位时+50% [ICON_PRODUCTION] 生产力。对矿山、采石场、伐木场、渔船、牧场、种植园和露营地发动掠夺或海岸扫荡时将获得 [ICON_SCIENCE] 科技值和 [ICON_CULTURE] 文化值。掠夺 [ICON_TradeRoute] 商路时的收益翻倍。解锁“航海术”科技后即可获得特色单位维京长船。"		),
("en_US",	    "LOC_TRAIT_LEADER_THUNDERBOLT_EXPANSION2_DESCRIPTION",		        "Allows coastal raiding for all naval melee units and +50% [ICON_PRODUCTION] Production toward all naval melee units. Receive [ICON_SCIENCE] Science and [ICON_CULTURE] Culture from pillaging and coastal raiding Mines, Quarries, Lumber mills, Fishing boats, Pastures, Plantations, and Camps. Gain double bonus from plunder of [ICON_TradeRoute] trade route. Gain the Viking Longship unique unit with Sailing."	),
("zh_Hans_CN",	"LOC_BUILDING_STAVE_CHURCH_DESCRIPTION",		        "挪威特色建筑。建成后方可使用 [ICON_Faith] 信仰值购买使徒和审判官。圣地区域可从树林获得额外的标准相邻加成。城市中每个海岸资源单元格+1 [ICON_PRODUCTION] 生产力。[NEWLINE][NEWLINE]其基础产出延伸到4个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"		),
("en_US",	    "LOC_BUILDING_STAVE_CHURCH_DESCRIPTION",		        "A building unique to Norway. Required to purchase Apostles and Inquisitors with [ICON_Faith] Faith. Holy Site districts get an additional standard adjacency bonus from Woods. +1 [ICON_PRODUCTION] Production to each coastal resource tile in this city.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 4 tiles that do not already have a bonus from a Holy Site Tier 2 building. "	),
("zh_Hans_CN",	"LOC_BUILDING_STAVE_CHURCH_HEROES_DESCRIPTION",		        "挪威特色建筑。建成后方可使用 [ICON_Faith] 信仰值购买使徒和审判官。圣地区域可从树林获得额外的标准相邻加成。城市中每个海岸资源单元格+1 [ICON_PRODUCTION] 生产力。在此城中召回英雄所花费的 [ICON_Faith] 信仰值-15%。[NEWLINE][NEWLINE]其基础产出延伸到4个单元格内的所有城市（前提是其尚未从其他的此等级建筑获得加成）。"		),
("en_US",	    "LOC_BUILDING_STAVE_CHURCH_HEROES_DESCRIPTION",		        "A building unique to Norway. Required to purchase Apostles and Inquisitors with [ICON_Faith] Faith. Holy Site districts get an additional standard adjacency bonus from Woods. +1 [ICON_PRODUCTION] Production to each coastal resource tile in this city. Recalling Heroes in this city costs 15% less [ICON_Faith] Faith.[NEWLINE][NEWLINE]Its base yields are extended to all City Centers within 4 tiles that do not already have a bonus from a Holy Site Tier 2 building."	),
("zh_Hans_CN",  "LOC_UNIT_NORWEGIAN_BERSERKER_DESCRIPTION",           "挪威特色的中世纪近战单位，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]若该单位从敌人领土开始回合，则 [ICON_Movement] 移动力为4点。攻击时+10 [ICON_Strength] 战斗力，防御时-5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_NORWEGIAN_BERSERKER_DESCRIPTION",           "Norwegian unique Medieval era Melee unit, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]4 [ICON_Movement] Movement if this unit starts in enemy territory. +10 [ICON_Strength] Combat Strength when attacking and -5 [ICON_Strength] Combat Strength when defending."),
--CIVILIZATION_NUBIA
("zh_Hans_CN",	"LOC_TRAIT_LEADER_KANDAKE_OF_MEROE_DESCRIPTION",		"所有城市建造区域时+30%建造速度。对于市中心与至少一个努比亚金字塔相邻的城市，此加成提高至60%。相邻加成不少于4的区域为城市提供10%的对应产出。"		),
("en_US",	    "LOC_TRAIT_LEADER_KANDAKE_OF_MEROE_DESCRIPTION",		"+30% [ICON_Production] Production towards all districts rising to +60% if there is a Nubian Pyramid adjacent to the City Center. +10% corresponding yields for city if district has a adjacency bonus that is no less than 4."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_TA_SETI_DESCRIPTION",		"游戏开始时便拥有“采矿业”和“弓箭”科技。远程单位+50% [ICON_Production] 生产力。所有远程单位战斗经验值+50%。战略资源矿产+1 [ICON_Production] 生产力。加成和奢侈品资源矿产+2 [ICON_Gold] 金币。与河流相邻或位于沙漠丘陵上的矿山+1 [ICON_Production] 生产力。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_TA_SETI_DESCRIPTION",		"Begin the game with 'Mining' and 'Archery' technology. +50% [ICON_Production] Production toward Ranged units. All Ranged units gain +50% combat experience. Mines over strategic resources provide +1 [ICON_Production] Production. Mines over bonus and luxury resources provide +2 [ICON_Gold] Gold. +1 [ICON_Production] for mine if it is adjacent to a river or if it is on a desert hills tile."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_PYRAMID_DESCRIPTION",		"解锁建造者建造努比亚金字塔的能力。[NEWLINE][NEWLINE]可从相邻区域获得大量产出。如与市中心相邻，+3 [ICON_Food] 食物。如与其他任意提供相邻加成的区域相邻，+3对应产出。[NEWLINE][NEWLINE]必须建在沙漠、沙丘或泛滥平原上。"		),
("en_US",	    "LOC_IMPROVEMENT_PYRAMID_DESCRIPTION",		"Improvement that unlocks with Masonry and must be built on Desert, Desert Hills or Floodplains. Receives huge yields from adjacent districts. +3 [ICON_Food] Food if adjacent to a city center. For all other districts that award adjacency bonuses: +3 of the appropriate yield if that district is adjacent."	),
("zh_Hans_CN",  "LOC_UNIT_NUBIAN_PITATI_DESCRIPTION",           "努比亚特色的远古时代远程单位，替代弓箭手，克制近战单位。视野为1。[NEWLINE][NEWLINE][ICON_Movement] 移动力大于弓箭手。"),
("en_US",       "LOC_UNIT_NUBIAN_PITATI_DESCRIPTION",           "Nubian unique Ancient era Ranged unit that replaces the Archer, counters Melee units well. Sight: 1 [NEWLINE][NEWLINE] Stronger than the Archer with extra [ICON_Movement] Movement."),
--CIVILIZATION_OTTOMAN
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GREAT_TURKISH_BOMBARD_DESCRIPTION",		"生产攻城单位时 [ICON_Production] 生产力+100%。攻击区域防御时，所有攻城单位+5 [ICON_Strength] 战斗力。征服城市不会造成 [ICON_Citizen] 人口损失。非奥斯曼建立的城市每回合+1 [ICON_AMENITIES] 宜居度和+4忠诚度。每次占领一个城市便获得1个攻城单位。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GREAT_TURKISH_BOMBARD_DESCRIPTION",		"+50% [ICON_Production] Production toward siege units. All siege units gain +5 [ICON_Strength] Combat Strength against district defenses. Conquered cities do not lose [ICON_Citizen] Population. Cities not founded by the Ottomans gain +1 [ICON_AMENITIES] Amenity and +4 Loyalty per turn. Gain a free Siege unit when you conquer a city."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_SULEIMAN_GOVERNOR_DESCRIPTION",		        "拥有军事与外交能力的专属特色总督。研究完“火药”科技后获得特色单位“苏丹亲兵”及1个 [Icon_Governor] 总督头衔。占领的城市+10%的所有类型产出。"		),
("en_US",	    "LOC_TRAIT_LEADER_SULEIMAN_GOVERNOR_DESCRIPTION",		        "Exclusive unique Governor with military and diplomatic abilities. Gain the Janissary unique unit and a [Icon_Governor] Governor Title when the Gunpowder technology is researched. +10% for all types of yields in conquered cities."	),
-- ("zh_Hans_CN",	"LOC_BUILDING_GRAND_BAZAAR_DESCRIPTION",		                "奥斯曼特色建筑。城市中改良的每种不同战略资源均可额外增加1点。城市改良的每个奢侈品资源均可提供1点 [ICON_AMENITIES] 宜居度。[NEWLINE]其 [ICON_Gold] 金币加成延伸到4单元格内的所有市中心（前提是其尚未从其他的此建筑获得加成）。[NEWLINE]若此城市拥有市政厅（郡县）或更高级建筑，则+1 [ICON_TradeRoute] 贸易路线容量。"		),
-- ("en_US",	    "LOC_BUILDING_GRAND_BAZAAR_DESCRIPTION",		                "A building unique to the Ottomans. Accumulate 1 extra Strategic resource for every different type of Strategic resource this city has improved. Receive 1 [ICON_AMENITIES] Amenity for every Luxury resource this city has improved. [NEWLINE]Its [ICON_Gold] Gold bonus is extended to all City Centers within 4 tiles that do not already have a bonus from this building type. [NEWLINE]+1 [ICON_TradeRoute] if city has Guildhall (County) or higher level of Guildhall building."	),
("zh_Hans_CN",  "LOC_UNIT_SULEIMAN_JANISSARY_DESCRIPTION",           "奥斯曼特色的文艺复兴时代近战单位，替代火枪手，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]初始便拥有一项免费升级。价格极低，城市在训练苏丹亲兵后将失去1点人口。"),
("en_US",       "LOC_UNIT_SULEIMAN_JANISSARY_DESCRIPTION",           "Ottoman unique Renaissance Era Melee unit that replaces the Musketman, counters Anti-Cavalry units well. Sight: 1 [NEWLINE][NEWLINE] Starts with a free promotion. Super cheap but a city where trains a Janissary will lose a population."),
--CIVILIZATION_PERSIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_SATRAPIES_DESCRIPTION",		        "可以使用 [ICON_Gold] 金币购买区域。目的地的每个专业化区域为国内 [ICON_TradeRoute] 贸易路线+1 [ICON_Gold] 金币，+1 [ICON_Culture] 文化值。本国领土中的道路提升一个等级。每个城市奖励1个 [ICON_Governor] 总督头衔。国际 [ICON_TradeRoute] 贸易路线的收益减半。 "		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_SATRAPIES_DESCRIPTION",		        "Can purchase district with [ICON_Gold] gold. +1 [ICON_Gold] Gold and +1 [ICON_Culture] Culture for each specialty district in target city for domestic [ICON_TradeRoute] trade routes. Roads built in your territory are one level more advanced than usual. Gain 1 [ICON_Governor] governor title for each city. Receive halved yields from international [ICON_TradeRoute] trade routes."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_FALL_BABYLON_EXPANSION2_DESCRIPTION",		"对主要文明宣布突袭战争后的10回合内+2 [ICON_Movement] 移动力，+5 [ICON_Strength] 战斗力。拥有驻扎单位的占领城市每回合的忠诚度+8。占领城市后奖励一个近战单位。"		),
("en_US",	    "LOC_TRAIT_LEADER_FALL_BABYLON_EXPANSION2_DESCRIPTION",		"+2 [ICON_Movement] Movement and +5 [ICON_Strength] combat strength for the first 10 turns after declaring a Surprise War on a major civilization. +8 Loyalty per turn in occupied cities with a garrisoned unit. Receive a free melee unit when you capture a city. "	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_PAIRIDAEZA_DESCRIPTION",		            "解锁建造者建造波斯庭院（波斯特色改良设施）的能力。[NEWLINE][NEWLINE]+1 [ICON_CULTURE] 文化值并+1 [ICON_GOLD] 金币。+1魅力。每个相邻圣地、剧院广场和娱乐中心+2 [ICON_CULTURE] 文化值。每个相邻军营、商业中心和市中心+2 [ICON_GOLD] 金币。解锁更多科技和市政树后可提供更多 [ICON_Culture] 文化值、[ICON_GOLD] 金币和 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]无法建在雪地、冻土、雪地丘陵或冻土丘陵上，也无法建在另一座波斯庭院旁。"		),
("en_US",	    "LOC_IMPROVEMENT_PAIRIDAEZA_DESCRIPTION",		            "Unlocks the Builder ability to construct a Pairidaeza, unique to Persia.[NEWLINE][NEWLINE]+1 [ICON_CULTURE] Culture and +1 [ICON_GOLD] Gold. +1 Appeal. +2 [ICON_CULTURE] Culture for each adjacent Holy Site, Entertainment  Complex, and Theater Square. +2 [ICON_GOLD] Gold for each adjacent Encampment, Commercial Hub, and City Center. Additional [ICON_GOLD] Gold, [ICON_Culture] Culture, and [ICON_Tourism] Tourism as you advance through the Technology and Civics Tree. [NEWLINE][NEWLINE]Cannot be built on Snow, Tundra, Snow Hills, or Tundra Hills or adjacent to another Pairidaeza."	),
("zh_Hans_CN",  "LOC_UNIT_PERSIAN_IMMORTAL_DESCRIPTION",           "波斯特色的古典时期近战单位，替代剑客，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]此近战单位可进行远程攻击，[ICON_Range] 范围为2。"),
("en_US",       "LOC_UNIT_PERSIAN_IMMORTAL_DESCRIPTION",           "Persian unique Classical era Melee unit that replaces the Swordsman, counters Anti-Cavalry units well. Sight: 1 [NEWLINE][NEWLINE]Melee class unit with a ranged attack, [ICON_Range] Range 2."),
--CIVILIZATION_PHOENICIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MEDITERRANEAN_COLONIES_DESCRIPTION",		"建立 [ICON_Capital] 首都后立即奖励一个开拓者，每次迁都后在新 [ICON_Capital] 首都额外奖励一个开拓者。与海岸单元格相邻的专业区域+100%对应相邻加成，但不与海岸单元格相邻的专业区域-100%对应相邻加成。腓尼基建造的海岸城市若与 [ICON_Capital] 首都在同一大陆，则其忠诚度为100%。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MEDITERRANEAN_COLONIES_DESCRIPTION",		"Grant a Settler after you settle your [ICON_Capital] Capital. Grant an addition Settler at the new [ICON_Capital] capital after you move your capital. +100% adjacency bonus for specialty districts that are adjacent to coast. However, -100% adjacency bonus for specialty districts that are not adjacent to coast. Coastal cities founded by Phoenicia and located on the same continent as the Phoenician [ICON_Capital] Capital are 100% Loyal."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_FOUNDER_CARTHAGE_DESCRIPTION",		"游戏开始便拥有“写作”科技。每个市政广场建筑与市政广场区域+1 [ICON_TradeRoute] 贸易路线容量。在首都和拥有市政广场的城市建造区域时+100% [ICON_Production] 生产力。起始城市中的每个海岸单元格为国际 [ICON_TradeRoute] 贸易路线提供+1 [ICON_GOLD] 金币。"		),
("en_US",	    "LOC_TRAIT_LEADER_FOUNDER_CARTHAGE_DESCRIPTION",		"Begin the game with 'Writing' technology. +1 [ICON_TradeRoute] Trade Route capacity for each Government Plaza building and the Government Plaza district. +100% [ICON_Production] Production toward districts in the capital city and in city with Government Plaza district. International [ICON_TradeRoute] Trade Routes gain +1 [ICON_GOLD] Gold for every Coast tile in the origin city."	),
("zh_Hans_CN",	"LOC_DISTRICT_COTHON_DESCRIPTION",		"腓尼基特色城市海军活动区域，取代港口且建造成本更低。同时移除该单元格的上船、下船 [ICON_Movement] 移动力减益。必须建在与陆地相邻的海岸或湖泊地形上。无法建造在礁石上。[NEWLINE][NEWLINE]与港口的区别在于，从相邻市中心，商业区和军营区获得超大量 [ICON_Gold] 金币加成，但不从资源获得相邻加成。该城市的海军单位的 [ICON_PRODUCTION] 生产力+50%。在该城范围内，所有受损海军单位每回合可恢复100点生命值。防御工事的攻击范围为2。不影响相邻单元格的魅力。"		),
("en_US",	    "LOC_DISTRICT_COTHON_DESCRIPTION",		"A district unique to Phoenicia for naval activity in your city. Replaces the Harbor district and cheaper to build. Also removes the [ICON_Movement] Movement penalty for embarking and disembarking to and from this tile. Must be built on Coast or Lake Terrain adjacent to land. Cannot be built on Reef. [NEWLINE][NEWLINE]Gain tremendous [ICON_Gold] gold adjacency bonus from City center, Commercial hub, and Encampment district. +50% [ICON_PRODUCTION] Production towards naval units in this city. All wounded naval units in this city’s borders heal +100 HP each turn. Attack range of defence is 2. No appeal penalty."	),
--CIVILIZATION_POLAND
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_GOLDEN_LIBERTY_DESCRIPTION",		"在友好领土内建成军营后对相邻单元格施放文化炸弹。每次进入黄金时代便奖励2个 [ICON_GreatWork_Relic] 遗物。当前政体的所有军事政策槽位将转换为通配符槽位。圣地从相邻区域获得大量 [ICON_Faith] 信仰值相邻加成（但无来自区域的其他加成）。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_GOLDEN_LIBERTY_DESCRIPTION",		"Culture Bomb adjacent tiles when completing an Encampment inside friendly territory. Gain two free [ICON_GreatWork_Relic] Relic when you enter a golden age. All Military policy slots in the current government are converted to Wildcard slots. Holy Sites gain large [ICON_Faith] Faith adjacency bonus from adjacent districts (but no other adjacency bonus from adjacent districts)."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_LITHUANIAN_UNION_DESCRIPTION",		    "如城市在波兰文化炸弹的攻击下失去单元格，则波兰的主流宗教将成为此城的主流宗教。首次使一座城市信奉你创立的宗教时+1时代得分。所有 [ICON_GreatWork_Relic] 遗物均提供+2所有类型产出。"		),
("en_US",	    "LOC_TRAIT_LEADER_LITHUANIAN_UNION_DESCRIPTION",		    "Poland's majority religion becomes the dominant religion in cities that loses a tile to a Polish Culture Bomb. +1 era score when a city is converted to your religion for the first time.  All [ICON_GreatWork_Relic] Relics provide bonus: +2 for all types of yields."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_LITHUANIAN_UNION_HEROES_DESCRIPTION",		"如相邻城市在波兰文化炸弹的攻击下失去单元格，则波兰创立的宗教将成为此城中的主流宗教。首次使一座城市信奉你创立的宗教时+1时代得分。所有 [ICON_GreatWork_Relic] 遗物和 [ICON_GreatWork_HeroRelic] 英雄遗物均提供+2所有类型产出。"		),
("en_US",	    "LOC_TRAIT_LEADER_LITHUANIAN_UNION_HEROES_DESCRIPTION",		"The Religion founded by Poland becomes the majority in an adjacent city that loses a tile to a Polish Culture Bomb. +1 era score when a city is converted to your religion for the first time. All [ICON_GreatWork_Relic] Relics and [ICON_GreatWork_HeroRelic] Heroic Relics provide bonus: +2 for all types of yields."	),
("zh_Hans_CN",	"LOC_POLAND_DISTRICT_DISTRICT_FAITH",		                "+{1_num} [ICON_Faith] 信仰值来自相邻区域。（贵族民主制）"		),
("en_US",	    "LOC_POLAND_DISTRICT_DISTRICT_FAITH",		                "+{1_num} [ICON_Faith] Faith from the adjacent district.(Golden Liberty)"	),
("zh_Hans_CN",  "LOC_UNIT_POLISH_HUSSAR_DESCRIPTION",           "波兰特色的中世纪重骑兵单位，克制远程单位。视野为2。[NEWLINE][NEWLINE]伤害点数高于敌方的状况下，会将防守的敌方单位推至后方的单元格。无法后退的防守单位会遭受额外伤害。"),
("en_US",       "LOC_UNIT_POLISH_HUSSAR_DESCRIPTION",           "Polish unique Medieval era Heavy Cavalry unit, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE] Pushes defending enemy units back from their hex in any battle where they score more damage. Defenders that cannot retreat suffer additional damage."),
--CIVILIZATION_PORTUGAL
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_PORTUGAL_DESCRIPTION",		                "国际 [ICON_TradeRoute] 贸易路线所有产出+50%，但必须起源于海岸城市，并只能通往处于海岸上或拥有港口的城市。商人单位在水域中的行动范围+50%，且解锁之后可立即在水域中行动。海上的商人无法被掠夺。解锁前置科技后立即获得“制图学”和“横帆装置”。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_PORTUGAL_DESCRIPTION",		                "International [ICON_TradeRoute] Trade Routes must originate from a coastal city and can only reach cities on the coast or with a Harbor, but receive +50% towards all yields. Trader units have +50% range over water, and can embark as soon as they are unlocked. Traders can not be plundered when they are on water tiles. Get 'Cartography' and 'Square rigging' techs immediately after researching the pre-tech."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_JOAO_III_DESCRIPTION",		                "所有单位+1视野。与1个文明相遇便+1 [ICON_TradeRoute] 贸易路线容量。对所有城邦开放边界。海外城市的每处奢侈品资源+4 [ICON_Gold] 金币、每处战略资源+3 [ICON_Production] 生产力。"		),
("en_US",	    "LOC_TRAIT_LEADER_JOAO_III_DESCRIPTION",		                "All units receive +1 sight. +1 [ICON_TradeRoute] Trade Route capacity when a civilization is met. Open Borders with all city-states. +4 [ICON_Gold] gold for luxury resources and +3 [ICON_Production] production for strategic resources in foreign cities."	),
-- ("zh_Hans_CN",	"LOC_BUILDING_NAVIGATION_SCHOOL_DESCRIPTION",		                "葡萄牙特色建筑。在此城中生产海军单位时+25%生产力。此城中每拥有2个海岸或湖泊单元格，则+1 [ICON_SCiENCE] 科技值。每回合+1 [ICON_GreatAdmiral] 海军统帅点数、+1 [ICON_GreatScientist] 大科学家点数。[NEWLINE][ICON_SCIENCE] 科技值加成延伸到4个单元格中的每个市中心(此加成每座城市仅能应用1次)。[NEWLINE]由于教育水平的提高，城市中每个 [ICON_Citizen] 公民提供+0.1 [ICON_SCIENCE] 科技值。"		),
-- ("en_US",	    "LOC_BUILDING_NAVIGATION_SCHOOL_DESCRIPTION",		                "A building unique to Portugal. +25% [ICON_PRODUCTION] Production towards naval units in this city. +1 [ICON_SCiENCE] Science for every two coast or lake tiles in this city. +1 [ICON_GreatAdmiral] Great Admiral and +1 [ICON_GreatScientist] Great Scientist point per turn. [NEWLINE]Bonus [ICON_SCIENCE] Science extends to each city center within 4 tiles. This bonus applies once to a city. [NEWLINE]+0.1 [ICON_SCIENCE] Science per [ICON_Citizen] Citizen in the city due to better education."	),
--CIVILIZATION_RUSSIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_MOTHER_RUSSIA_EXPANSION2_DESCRIPTION",		"建立城市可获得额外8格领土。冻土提供+1 [ICON_Faith] 信仰值与+1 [ICON_Production] 生产力。你的单位不会受到暴风雪的伤害。与俄罗斯交战的文明单位如处于俄罗斯领土中，遭遇暴风雪时受到的伤害+100%。冻土和雪地地形上的战略资源的累积速度翻倍。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_MOTHER_RUSSIA_EXPANSION2_DESCRIPTION",		"Extra territory upon founding cities. +1 [ICON_Faith] Faith and +1 [ICON_Production] Production from Tundra. Units do not receive damage from Blizzards. Civilizations that are at war with Russia receive +100% unit damage from Blizzards in Russian territory. In Snow, Tundra, Snow Hills, and Tundra Hills, strategic resource accumulation rate is +100%."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GRAND_EMBASSY_DESCRIPTION",		                "与比俄罗斯更先进的文明建立 [ICON_TradeRoute] 贸易路线，从中可获得 [ICON_Science]  科技值或 [ICON_Culture] 文化值。（每高三个科技或市政加1点）间谍窃取科技和巨作时的行动时间减半，窃取科技成功后获得额外的晋升。"		),
("en_US",	    "LOC_TRAIT_LEADER_GRAND_EMBASSY_DESCRIPTION",		                "Receives [ICON_Science] Science or [ICON_Culture] Culture from [ICON_TradeRoute] Trade Routes to civilizations that are more advanced than Russia (+1 per 3 technologies or civics ahead) Spy operations take half time for stealing tech and heisting great work. Spies who steal a tech boost without being detected gain an extra boost."	),
("zh_Hans_CN",	"LOC_DISTRICT_LAVRA_DESCRIPTION",		                "俄罗斯特色宗教活动区域，代替圣地，建造费用更低。[NEWLINE][NEWLINE]与圣地的区别在于，从相邻冻土和雪地获得少量 [ICON_Faith] 信仰值相邻加成，但不从森林和丛林获得相邻加成。产出额外的 [ICON_GreatProphet] 大预言家点数。拥有神社的修道院每回合提供+1 [ICON_GreatWriter] 大作家点数；拥有寺庙的修道院每回合提供+1 [ICON_GreatArtist] 大艺术家点数；拥有祭祀建筑的修道院每回合提供+1 [ICON_GreatMusician] 大音乐家点数。此城市每消耗一位伟人，城市边界则扩张1单元格。[NEWLINE]为相邻单元格提供+1魅力。"		),
("en_US",	    "LOC_DISTRICT_LAVRA_DESCRIPTION",		                "A district unique to Russia for religious activity. Replaces the Holy Site district and cheaper to build.[NEWLINE][NEWLINE]Gain minor [ICON_Faith] Faith adjacency bonus from Tundra and Snow tiles. The Lavra provides +1 [ICON_GreatWriter] Great Writer point per turn with a Shrine, +1 [ICON_GreatArtist] Great Artist point per turn with a Temple, and +1 [ICON_GreatMusician] Great Musician point per turn with a Worship building. Your city border grows by one tile each time a Great Person is expended in this city. [NEWLINE]+1 Appeal."	),
("zh_Hans_CN",  "LOC_UNIT_RUSSIAN_COSSACK_DESCRIPTION",           "俄罗斯特色的工业时代轻骑兵单位，替代骑兵，克制攻城单位。视野为2。[NEWLINE][NEWLINE]比骑兵更强大，位于或靠近俄罗斯领土作战时，+5 [ICON_Strength] 战斗力。如有剩余 [ICON_Movement] 移动力，可在攻击后移动。"),
("en_US",       "LOC_UNIT_RUSSIAN_COSSACK_DESCRIPTION",           "Russian unique Industrial era Light Cavalry unit that replaces Cavalry, counters Siege units well. Sight: 2[NEWLINE][NEWLINE]Stronger than Cavalry, and gains +5 [ICON_Strength] Combat Strength when fighting in or adjacent to its home territory. Can move after attacking if [ICON_Movement] Movement points remain."),
--CIVILIZATION_ROME
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_ALL_ROADS_TO_ROME_DESCRIPTION",		"所有您创建或攻占的城市一开始便拥有贸易站。如果位于您 [ICON_Capital] 首都的 [ICON_TradeRoute] 贸易路线范围内，城市自动建立一条通至罗马的路。您的 [ICON_TradeRoute] 贸易路线经过您的城市贸易站+1 [ICON_Gold] 金币。只要拥有比敌方更高的 [ICON_VisLimited] 外交能见度，所有单位来自[ICON_VisLimited] 外交能见度的基础 [ICON_Strength] 战斗力加成翻倍。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_ALL_ROADS_TO_ROME_DESCRIPTION",		"All cities you found or conquer start with a Trading Post. If in [ICON_TradeRoute] Trade Route range of your [ICON_Capital] Capital, they also start with a road to it. Your [ICON_TradeRoute] Trade Routes earn +1 [ICON_Gold] Gold for passing through Trading Posts in your own cities. All units double the usual [ICON_Strength] Combat Bonus for having a higher level of [ICON_VisLimited] Diplomatic Visibility than their opponent. "	),
("zh_Hans_CN",	"LOC_DISTRICT_BATH_EXPANSION2_ALT_DESCRIPTION",		"促进城市发展的罗马特色区域。取代水渠，建造费用更低。[NEWLINE][NEWLINE]城市人口增长速度30%，为农场+1 [ICON_Food] 食物。从相邻河流、湖泊、绿洲或山脉引水，为该城市提供淡水资源。使没有淡水资源的城市拥有8个 [ICON_Housing] 住房。已有淡水资源的城市则+3 [ICON_Housing] 住房。在任何情况下，浴场都能额外提供+2 [ICON_Housing] 住房和+1 [ICON_Amenities] 宜居度。干旱时可防止 [ICON_Food] 食物损失。必须修建在与市中心相邻的单元格中。"		),
("en_US",	    "LOC_DISTRICT_BATH_EXPANSION2_ALT_DESCRIPTION",		"A district unique to Rome for city growth. Replaces the Aqueduct district and cheaper to build.[NEWLINE][NEWLINE]City growth rate increase 30%, +1 [ICON_Food] Food to Farms. It provides this city with a source of fresh water from an adjacent River, Lake, Oasis, or Mountain. Cities that do not yet have existing fresh water receive up to 8 [ICON_Housing] Housing. Cities that already have existing fresh water will instead get +3 [ICON_Housing] Housing. In all cases the Bath provides an additional bonus of +2 [ICON_Housing] Housing and +1 [ICON_Amenities] Amenity. Prevents [ICON_Food] Food loss during Drought. Must be built adjacent to the City Center."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_TRAJANS_COLUMN_DESCRIPTION",		"所有城市建立时带有一个额外的市中心建筑。（远古时代是纪念碑）。位于市中心第三环的单元格+1 [ICON_PRODUCTION] 生产力+1 [ICON_GOLD] 金币（不叠加）。城市获得新单元格的速度加快50%。"		),
("en_US",	    "LOC_TRAIT_LEADER_TRAJANS_COLUMN_DESCRIPTION",		"All cities start with an additional City Center building. (Starts with a Monument building in the Ancient era). +1 [ICON_PRODUCTION] production and +1 [ICON_GOLD] gold for tiles on the third ring of a city center. +50% culture border expansion rate for all cities."	),
("zh_Hans_CN",  "LOC_UNIT_ROMAN_LEGION_DESCRIPTION",           "罗马特色的古典时期近战单位，替代剑客，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]能建造一个罗马堡垒。"),
("en_US",       "LOC_UNIT_ROMAN_LEGION_DESCRIPTION",           "Roman unique Classical era Melee unit that replaces the Swordsman, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]Can build a Roman Fort."),
--CIVILIZATION_SCOTLAND
-- ("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_SCOTTISH_ENLIGHTENMENT_DESCRIPTION",		"幸福度为快乐的城市额外+7% [ICON_SCIENCE] 科技值、 [ICON_GOLD] 金币、 [ICON_PRODUCTION] 生产力。快乐的城市中的学院+1 [ICON_GreatScientist] 大科学家点数、工业区+1 [ICON_GreatEngineer] 大工程师点数、商业中心+1 [ICON_GreatMERCHANT] 大商人点数。欣喜若狂的城市获得的加成则加倍。"		),
-- ("en_US",	    "LOC_TRAIT_CIVILIZATION_SCOTTISH_ENLIGHTENMENT_DESCRIPTION",		"Happy cities receive an additional +7%  [ICON_SCIENCE] Science, [ICON_GOLD] GOLD, and [ICON_PRODUCTION] Production. Happy cities generate +1  [ICON_GreatScientist] Great Scientist point per Campus, +1 [ICON_GreatMERCHANT] Great Merchant point per Commercial Hub, and +1 [ICON_GreatEngineer] Great Engineer point per Industrial Zone. Ecstatic cities double all these amounts."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_SCOTTISH_ENLIGHTENMENT_DESCRIPTION",		"[ICON_Amenities] 宜居度不少于1的城市额外+5% [ICON_SCIENCE] 科技值、 [ICON_GOLD] 金币、 [ICON_PRODUCTION] 生产力，且学院+1 [ICON_GreatScientist] 大科学家点数、工业区+1 [ICON_GreatEngineer] 大工程师点数、商业中心1 [ICON_GreatMERCHANT] 大商人点数。[ICON_Amenities] 宜居度不少于5的城市中，这些加成为两倍。[ICON_Amenities] 宜居度不少于9的城市中，这些加成为三倍。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_SCOTTISH_ENLIGHTENMENT_DESCRIPTION",		"Cities with no less then 1 [ICON_Amenities] amenities receive an additional +5%  [ICON_SCIENCE] Science, [ICON_GOLD] GOLD, and [ICON_PRODUCTION] Production. Additionally, +1 [ICON_GreatScientist] Great Scientist point per Campus, +1 [ICON_GreatMERCHANT] Great Merchant point per Commercial Hub, and +1 [ICON_GreatEngineer] Great Engineer point per Industrial Zone. Double all these amounts in cities with no less then 5 [ICON_Amenities] amenities. Triple all these amounts in cities with no less then 9 [ICON_Amenities] amenities."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_BANNOCKBURN_DESCRIPTION",		"解锁防御战术市政后即可宣布解放战争。宣布解放战争后的10回合内+100% [ICON_Production] 生产力、+2 [ICON_Movement] 移动力。相邻加成不少于2的专业化区域+1 [ICON_Housing] 住房，相邻加成不少于4的专业化区域+1 [ICON_Amenities] 宜居度。"		),
("en_US",	    "LOC_TRAIT_LEADER_BANNOCKBURN_DESCRIPTION",		"Can declare a War of Liberation after gaining the Defensive Tactics Civic. +100% [ICON_Production] Production and +2 [ICON_Movement] Movement for the first 10 turns after declaring a Liberation War. +1 [ICON_Housing] Housing for Specialty districts with no less than 2 corresponding adjacency bonus, +1 [ICON_Amenities] Amenity for Specialty districts with no less than 4 corresponding adjacency bonus."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_GOLF_COURSE_XP2_DESCRIPTION",		"解锁建造者建造高尔夫球场（苏格兰特色改良设施）的能力。[NEWLINE][NEWLINE]+2 [ICON_AMENITIES] 宜居度，+2魅力，+5 [ICON_GOLD] 金币。与农场，牧场，营地，种植园相邻时，获得 [ICON_Culture] 文化值加成。[NEWLINE][NEWLINE]无法建在沙漠或沙漠丘陵地貌上。每座城市仅限建造一座，拥有高尔夫球场的单元格无法切换。"		),
("en_US",	    "LOC_IMPROVEMENT_GOLF_COURSE_XP2_DESCRIPTION",		"Unlocks the Builder ability to construct a Golf Course, unique to Scotland.[NEWLINE][NEWLINE]+2 [ICON_AMENITIES] Amenity, +2 Appeal, and +5 [ICON_GOLD] Gold. Provides extra [ICON_Culture] Culture for each Farm, Pasture, Plantation, and Camps it is adjacent to.[NEWLINE][NEWLINE]Cannot be placed on Desert or Desert Hills. One per city. Tiles with Golf Courses cannot be swapped. "	),
("zh_Hans_CN",  "LOC_UNIT_SCOTTISH_HIGHLANDER_DESCRIPTION",           "工业时代的苏格兰特色侦察单位，替代游骑兵。视野为5。[NEWLINE][NEWLINE]在丘陵和森林地貌作战时+5 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_SCOTTISH_HIGHLANDER_DESCRIPTION",           "Scottish unique Industrial Era unit that replaces the Ranger. Sight: 5 [NEWLINE][NEWLINE]+5 [ICON_Strength] Combat Strength bonus when fighting in Hill and Forest terrain."),
--CIVILIZATION_SCYTHIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_EXTRA_LIGHT_CAVALRY_DESCRIPTION",		"游戏开始时便拥有“畜牧业”和“骑马”科技。每回合获得4个 [ICON_RESOURCE_HORSES] 马。牧场+2 [ICON_Production] 生产力。每次您训练一个轻骑兵或萨卡弓骑手，都可以再获得一个轻骑兵或萨卡弓骑手。无法训练或购买开拓者，占领一座城市后奖励一个开拓者。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_EXTRA_LIGHT_CAVALRY_DESCRIPTION",		"Begin the game with 'Animal Husbandry' and 'Horseback Riding' techs. Gain 4 [ICON_RESOURCE_HORSES] Horses per turn. +2 [ICON_Production] Production for Pasture. Receive a second light cavalry unit or Saka Horse Archer each time you train a light cavalry unit or Saka Horse Archer. Can not train or purchase Settler. Gain a free Settler when you capture a city."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_KILLER_OF_CYRUS_DESCRIPTION",		            "攻击受伤单位时，所有单位+5 [ICON_Strength] 战斗力。消灭一个单位时恢复30点生命值。占领一座敌方城市后的5回合内，所有城市+50% [ICON_Production] 生产力。"		),
("en_US",	    "LOC_TRAIT_LEADER_KILLER_OF_CYRUS_DESCRIPTION",		            "All units receive +5 [ICON_Strength] Combat Strength when attacking wounded units. When they eliminate a unit, they heal up to 30 hit points. +50% [ICON_Production] Production for all cities for 5 turns after capturing an enemy city."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_KURGAN_DESCRIPTION",		                    "解锁建造者建造斯基泰特色坟墩的能力。[NEWLINE][NEWLINE]+2 [ICON_Faith] 信仰值。+2 [ICON_Gold] 金币。每个相邻牧场+2 [ICON_Faith] 信仰值，每个相邻军营区+2 [ICON_Gold] 金币。不能建在丘陵上。"		),
("en_US",	    "LOC_IMPROVEMENT_KURGAN_DESCRIPTION",		                    "Unlocks the Builder ability to construct a Kurgan, unique to Scythia.[NEWLINE][NEWLINE]+2 [ICON_Faith] Faith. +2 [ICON_Gold] Gold. +2 [ICON_Faith] Faith for each adjacent Pasture. +2 [ICON_Gold] Gold for each adjacent Encampment District. Cannot be built on Hills."	),
("zh_Hans_CN",  "LOC_UNIT_SCYTHIAN_HORSE_ARCHER_DESCRIPTION",           "斯基泰特色的古典时期火力支援单位，克制近战单位。视野为2。[NEWLINE][NEWLINE] [ICON_Range] 射程为1，[ICON_Movement] 移动力为4。"),
("en_US",       "LOC_UNIT_SCYTHIAN_HORSE_ARCHER_DESCRIPTION",           "Scythian unique Classical era Firesupport unit, counters Melee units well. Sight: 2 [NEWLINE][NEWLINE]4 [ICON_Movement] Movement, [ICON_Range] Range 1."),
--CIVILIZATION_SPAIN
("zh_Hans_CN",	"LOC_TRAIT_LEADER_EL_ESCORIAL_EXPANSION2_DESCRIPTION",		    "审判官可额外进行一次“清除异教”操作。审判官能完全铲除其他宗教的势力。与信仰其他宗教的单位进行战斗时，战斗和宗教单位+5 [ICON_Strength] 战斗力。你的宗教压力不会因为神学战斗失败而减少，宗教单位忽略地形和地貌的 [ICON_Movement] 移动力消耗。"		),
("en_US",	    "LOC_TRAIT_LEADER_EL_ESCORIAL_EXPANSION2_DESCRIPTION",		    "Inquisitors can Remove Heresy one extra time. Inquisitors eliminate 100% of the presence of other Religions. Combat and Religious units have a bonus of +5 [ICON_Strength] Combat Strength against players following other Religions. Your Religion's pressure never drops due to losses in Theological Combat. Religious units ignore [ICON_Movement] Movement costs of terrain and features."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_TREASURE_FLEET_EXPANSION2_DESCRIPTION",		"解锁“重商主义”后（而非“民族主义”和“动员”）即可组建舰队和无敌舰队。[ICON_TradeRoute] 贸易路线+3 [ICON_Gold] 金币、+2 [ICON_Faith] 信仰值、+1 [ICON_Production] 生产力。如 [ICON_TradeRoute] 贸易路线横跨多个大陆，则这些数值将乘以3倍。如城市处于非原始首都所在大陆，其建成时将获得1名建造者，建造区域时+25% [ICON_Production] 生产力。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_TREASURE_FLEET_EXPANSION2_DESCRIPTION",		"May form Fleets and Armadas with Mercantilism, instead of Nationalism and Mobilization. [ICON_TradeRoute] Trade Routes receive +3 [ICON_Gold] Gold, +2 [ICON_Faith] Faith, and +1 [ICON_Production] Production. [ICON_TradeRoute] Trade Routes between multiple continents receive triple these numbers. Cities not on your original Capital’s continent receive +25% [ICON_Production] Production towards districts and a builder when founded."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_MISSION_EXPANSION2_DESCRIPTION",		"解锁建造者建造西班牙特色改良设施传教团的能力。[NEWLINE][NEWLINE]+2 [ICON_Faith] 信仰值。地处与 [ICON_Capital] 首都不同的大陆时，+2 [ICON_Faith] 信仰值、+1 [ICON_PRODUCTION] 生产力，并+1 [ICON_Food] 食物。与每个学院和圣地区域相邻时，产出的 [ICON_Science] 科技值+1。获得“文化遗产”市政后还将产出更多 [ICON_SCIENCE] 科技值。[NEWLINE]不在原 [ICON_Capital] 首都所在大陆上、且拥有与市中心相邻时，城市每回合的忠诚度+5，且城市人口增长速度+20%。"		),
("en_US",	    "LOC_IMPROVEMENT_MISSION_EXPANSION2_DESCRIPTION",		"Unlocks the Builder ability to construct a Mission, unique to Spain.[NEWLINE][NEWLINE]+2 [ICON_Faith] Faith. +2 [ICON_Faith] Faith, +1 [ICON_PRODUCTION] Production, and +1 [ICON_FOod] Food if on a different continent than your [ICON_Capital] Capital. +1 [ICON_Science] Science for every adjacent Campus and Holy Site district. Additional [ICON_SCIENCE] Science once Cultural Heritage is discovered. [NEWLINE]+5 Loyalty per turn and +20% city growth rate for cities with a Mission improvement adjacent to the City Center and not on your original [ICON_Capital] Capital's continent."	),
("zh_Hans_CN",  "LOC_UNIT_SPANISH_CONQUISTADOR_DESCRIPTION",           "西班牙特色的文艺复兴时代近战单位，替代火枪手，克制抗骑兵单位。视野为1。[NEWLINE][NEWLINE]与宗教单位处于同一格位时+10 [ICON_Strength] 战斗力。如果该单位占领城市、或在城市被占领时处于相邻的单元格，该城市会自动将该单位所属玩家的宗教采纳为主流宗教。"),
("en_US",       "LOC_UNIT_SPANISH_CONQUISTADOR_DESCRIPTION",           "Spanish unique Renaissance era Melee unit that replaces the Musketman, counters Anti-Cavalry units well. Sight: 1[NEWLINE][NEWLINE]+10 [ICON_Strength] Combat Strength when there is a religious unit in the same hex. If this unit captures a city or is adjacent to a city when it is captured, the city will automatically adopt the Conquistador player's Religion as the dominant Religion."),
--CIVILIZATION_SUMERIA
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_FIRST_CIVILIZATION_DESCRIPTION",		    "与蛮族战斗时+10 [ICON_Strength] 战斗力。当您占领一个蛮族哨站时，您会获得部落村庄奖励，以及 [ICON_Gold] 金币。征用城邦单位费用降低一半。每座河流旁的城市+1 [ICON_Amenities] 宜居度+1 [ICON_Housing] 住房且奖励一个 [ICON_Envoy] 使者。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_FIRST_CIVILIZATION_DESCRIPTION",		    "+10 [ICON_Strength] Combat strength against Barbarian. When you capture a Barbarian Outpost, receive a Tribal Village reward in addition to the usual [ICON_Gold] Gold. Pay half the usual cost to levy city-state units. +1 [ICON_Amenities] Amenity, +1 [ICON_Housing] Housing, and +1 [ICON_Envoy] Envoy from each city that is near a river."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_ADVENTURES_ENKIDU_EXPANSION2_DESCRIPTION",		"与本方及本方盟友的交战方作战时+5 [ICON_Strength] 战斗力。[ICON_CivicBoosted] 鼓舞提供市政79%的 [ICON_Culture] 文化值。剧院广场建筑的 [ICON_Culture] 文化值基础产出-75%。"		),
("en_US",	    "LOC_TRAIT_LEADER_ADVENTURES_ENKIDU_EXPANSION2_DESCRIPTION",		"+5 [ICON_Strength] Combat Strength against units of players at war with you and your ally. [ICON_CivicBoosted] Inspirations provide 79% of the [ICON_Culture] Culture for civics. -75% [ICON_Culture] Culture base yield for buildings in Theater."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_GILGAMESH_HEROES_DESCRIPTION",		"与本方及本方盟友的交战方作战时+5 [ICON_Strength] 战斗力。进行招募英雄的项目时+100% [ICON_Production] 生产力；英雄的 [ICON_Lifespan] 寿命+25%。[ICON_CivicBoosted] 鼓舞提供市政79%的 [ICON_Culture] 文化值。剧院广场建筑的 [ICON_Culture] 文化值基础产出-75%。"		),
("en_US",	    "LOC_TRAIT_LEADER_GILGAMESH_HEROES_DESCRIPTION",		"+5 [ICON_Strength] Combat Strength against units of players at war with you and your ally. +100% [ICON_Production] Production increase when claiming Heroes, and Heroes have 25% longer [ICON_Lifespan] Lifespans. [ICON_CivicBoosted] Inspirations provide 79% of the [ICON_Culture] Culture for civics. -75% [ICON_Culture] Culture base yield for buildings in Theater."	),
("zh_Hans_CN",  "LOC_UNIT_SUMERIAN_WAR_CART_DESCRIPTION",           "苏美尔特色的远古时代重骑兵单位，克制远程单位。视野为2。[NEWLINE][NEWLINE]比其他所有初始单位更强大。对战抗骑兵单位时，无减益。如果该单位从开阔地形开始回合， [ICON_Movement] 移动力为4。"),
("en_US",       "LOC_UNIT_SUMERIAN_WAR_CART_DESCRIPTION",           "Sumerian unique Ancient era Heavy Cavalry unit, counters Ranged units well. Sight: 2[NEWLINE][NEWLINE]Stronger than all other starting units. No penalties against anti-cavalry units. 4 [ICON_Movement] Movement if this unit starts in open terrain."),
--CIVILIZATION_SWEDEN
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_NOBEL_PRIZE_DESCRIPTION",		"瑞典招募伟人时可获得100点 [ICON_Favor] 外交支持（标准速度下）。工业区、学院、商业中心的二级建筑获得以下加成：建造速度、基本产出、和伟人点数加倍，建造完成后为城市提供+1 [ICON_Citizen] 人口、+1 [ICON_Housing] 住房、和+1 [ICON_Amenities] 宜居度。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_NOBEL_PRIZE_DESCRIPTION",		"Sweden gains 100 [ICON_Favor] Diplomatic Favor when earning a Great Person (on Standard Speed). Tier 2 buildings of Industrial Zone, Campus, and Commercial Hub provide halved construction time, double base yields, and double great person points. +1 [ICON_Citizen] Citizen, +1 [ICON_Housing] Housing, and +1 [ICON_Amenities] Amenity for city upon finishing these buildings."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_KRISTINA_AUTO_THEME_DESCRIPTION",		"拥有3个或以上巨作槽位的建筑和2个或以上巨作槽位的奇观全部槽位填满时，可自动获得主题化加成。建筑主题化时,其中的所有巨作的基本产出和 [ICON_Tourism] 旅游业绩额外+100%。可在市政广场中建造女王图书馆。"		),
("en_US",	    "LOC_TRAIT_LEADER_KRISTINA_AUTO_THEME_DESCRIPTION",		"Buildings with at least three Great Work slots and wonders with at least two Great Work slots are automatically themed when they have all their slots filled. +100% yields bonus and +100% [ICON_Tourism] tourism bonus for all great works in themed buildings. She may construct the Queen's Bibliotheque in the Government Plaza."	),
("zh_Hans_CN",	"LOC_BUILDING_QUEENS_BIBLIOTHEQUE_DESCRIPTION",		"瑞典特色建筑。该建筑包含6个槽位，可放置任何类型的巨作，且产出大量文化类 [ICON_GreatPerson] 伟人点数。[NEWLINE]奖励1个 [Icon_Governor] 总督头衔。"		),
("en_US",	    "LOC_BUILDING_QUEENS_BIBLIOTHEQUE_DESCRIPTION",		"A building unique to Sweden. This building provides 6 greatwork slots for any type of greatwork.[NEWLINE]Awards +1 [Icon_Governor] Governor Title."	),
("zh_Hans_CN",	"LOC_IMPROVEMENT_OPEN_AIR_MUSEUM_DESCRIPTION",		"解锁建造者建造瑞典特色改良设施生态博物馆的能力。[NEWLINE][NEWLINE]每种地貌（雪地、冻土、沙漠、平原或草原）中的每座瑞典城市+2 [ICON_Culture] 文化值、+2 [ICON_Science] 科技值、+2 [ICON_Gold] 金币、+2 [ICON_Tourism] 旅游业绩。[NEWLINE][NEWLINE]每个城市仅限建造一座。生态博物馆所在的单元格无法被替换更改。"		),
("en_US",	    "LOC_IMPROVEMENT_OPEN_AIR_MUSEUM_DESCRIPTION",		"Unlocks the Builder ability to construct an Open-Air Museum, unique to Sweden.[NEWLINE][NEWLINE]+2 [ICON_Culture] Culture, +2 [ICON_Science] Science, +2 [ICON_Gold] Gold, and +2 [ICON_Tourism] Tourism for each type of terrain (Snow, Tundra, Desert, Plains or Grassland) in which at least one Swedish city is founded. [NEWLINE][NEWLINE]One per city. Tiles with Open-Air Museums cannot be swapped."	),
("zh_Hans_CN",  "LOC_UNIT_SWEDEN_CAROLEAN_DESCRIPTION",           "瑞典特色的文艺复兴时代抗骑兵单位，替代枪刺与射击方阵，克制骑兵单位。视野为1。[NEWLINE][NEWLINE]速度较其更快。每点未使用的 [ICON_Movement] 移动力提供+3 [ICON_Strength] 战斗力。"),
("en_US",       "LOC_UNIT_SWEDEN_CAROLEAN_DESCRIPTION",           "Swedish unique Renaissance Era Anti-Cavalry unit that replaces the Pike and Shot, counters Cavalry units well. Sight: 1 [NEWLINE][NEWLINE]Faster than the Pike and Shot. +3 [ICON_Strength] Combat Strength per unused [ICON_Movement] Movement."),
--CIVILIZATION_VIETNAM
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_VIETNAM_DESCRIPTION",		"所有陆地专业化区域只能建造在雨林、沼泽或树林上。与森林或雨林相邻时，专业化区域提供+50%对应的区域相邻加成，同时相邻则+100%。建在这些地貌上的建筑将获得以下收益：建在树林上提供+1 [ICON_CULTURE] 文化值、建在雨林上提供+1 [ICON_SCIENCE] 科技值、建在沼泽上提供+1 [ICON_PRODUCTION] 生产力。拥有“防御战术”市政后即可栽种树林。"		),
("en_US",	    "LOC_TRAIT_CIVILIZATION_VIETNAM_DESCRIPTION",		"All land specialty districts can only be built on Rainforest, Marsh, or Woods. +50% corresponding district adjacency bonuses for specialty districts that are adjacent to Forest or Jungle, double when adjacent to both. Receive the following yields for every building on these features: +1 [ICON_CULTURE] Culture in Woods, +1 [ICON_SCIENCE] Science in Rainforest and +1 [ICON_PRODUCTION] Production in Marsh. Woods can be planted with the Defensive Tactics civic."	),
("zh_Hans_CN",	"LOC_TRAIT_LEADER_TRIEU_DESCRIPTION",		"在雨林、沼泽或树林单元格中战斗的单位+5 [ICON_Strength] 战斗力。如果单位在回合开始时处于这些地貌中，则+1 [ICON_Movement] 移动力。而如果这些地貌单元格是您的领土，则以上加成均加倍。陆地单位击杀主要文明或城邦单位时，获得等同于对方单位战斗力25%的 [ICON_GreatGeneral] 大将军点数。"		),
("en_US",	    "LOC_TRAIT_LEADER_TRIEU_DESCRIPTION",		"+5 [ICON_Strength] Combat Strength for units fighting in Rainforest, Marsh, or Woods tiles. +1 [ICON_Movement] if they begin their turn there. Both these bonuses are doubled if the tile is your territory. Receive [ICON_GreatGeneral] Great General points when a land unit defeats a major or minor civilization's unit equal to 25% of the opposing unit's strength (Standard Speed)."	),
("zh_Hans_CN",	"LOC_ABILITY_TRIEU_GREAT_GENERAL_INFLUENCE_BONUS_DESCRIPTION",		"击杀主要文明或城邦单位时，获得等同于对方单位战斗力25%的 [ICON_GreatGeneral] 大将军点数。"		),
("en_US",	    "LOC_ABILITY_TRIEU_GREAT_GENERAL_INFLUENCE_BONUS_DESCRIPTION",		"Receive [ICON_GreatGeneral] Great General points when defeats a major or minor civilization's unit equal to 25% of the opposing unit's strength (Standard Speed)."	),
("zh_Hans_CN",	"LOC_DISTRICT_THANH_DESCRIPTION",		"越南特色区域，替代“军营”。每与1个区域相邻，则+2 [ICON_CULTURE] 文化值。研究完“飞行”科技后，将获得与 [ICON_CULTURE] 文化值产量相等的 [ICON_TOURISM] 旅游业绩。此区域不需要人口、建造花费更低、无法建在市中心周围，也并非专业化区域。[NEWLINE][NEWLINE]与农场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与牧场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与种植园相邻时+1 [ICON_Housing] 住房。[NEWLINE]与营地相邻时+1 [ICON_Housing] 住房。[NEWLINE]与城池相邻时+1 [ICON_Housing] 住房。（需“城市圈”游戏模式）[NEWLINE]与运河相邻时+1 [ICON_Housing] 住房。[NEWLINE]与航空港相邻时+1 [ICON_Housing] 住房。[NEWLINE]与市政广场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与国家军营相邻时+3 [ICON_Housing] 住房。"		),
("en_US",	    "LOC_DISTRICT_THANH_DESCRIPTION",		"A district unique to Vietnam which replaces the Encampment. +2 [ICON_CULTURE] Culture for each adjacent district. After Flight is researched receive [ICON_TOURISM] Tourism equal to the [ICON_CULTURE] Culture output. This district does not require population, is cheaper to build, cannot be built adjacent to the city center, and is not a specialty district. [NEWLINE][NEWLINE]+1 [ICON_Housing] Housing when adjacent to Farm.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Pasture.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Plantation.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Camp.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Thanh.(Requries Megalopolis game mode)[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Canal.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Aerodrome.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Government plaza.[NEWLINE]+3 [ICON_Housing] Housing when adjacent to National Encampment."	),
("zh_Hans_CN",  "LOC_UNIT_VIETNAMESE_VOI_CHIEN_DESCRIPTION",           "越南特色中世纪远程单位，克制近战单位。视野为3。[NEWLINE][NEWLINE]其攻击后可继续移动，拥有额外的移动力。此单位防御时战斗力更强、花费更高、且拥有更高的视野。"),
("en_US",       "LOC_UNIT_VIETNAMESE_VOI_CHIEN_DESCRIPTION",           "Vietnamese unique Medieval era ranged unit, counters Melee units well. Sight: 3 [NEWLINE][NEWLINE] They can move after attacking and have additional movement. These units are also stronger when defending, more expensive, and have greater sight."),
--CIVILIZATION_ZULU
("zh_Hans_CN",	"LOC_TRAIT_LEADER_AMABUTHO_DESCRIPTION",		"可更早组建军团（需雇佣兵市政）和军队（需民族主义市政）。使军团和军队的 [ICON_STRENGTH] 基础战斗力额外+5。训练抗骑兵单位时+100% [ICON_Production] 生产力。"		),
("en_US",	    "LOC_TRAIT_LEADER_AMABUTHO_DESCRIPTION",		"May form Corps (Mercenaries Civic) and Armies (Nationalism Civic) earlier. An additional +5 [ICON_STRENGTH] Base Combat Strength to both Corps and Armies. +100% [ICON_Production] production toward training anti-cavalry units."	),
("zh_Hans_CN",	"LOC_DISTRICT_IKANDA_DESCRIPTION",		"祖鲁特色区域，替代军营，防御工事的攻击范围为2。解锁相应的市政或科技后即可生产军团和军队。创建军团和军队的速度更快。伊坎达中的建筑+2 [ICON_GOLD] 金币、+1 [ICON_SCIENCE] 科技值。可靠近市中心建造，不会影响相邻单元格的魅力值。[NEWLINE][NEWLINE]与农场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与牧场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与种植园相邻时+1 [ICON_Housing] 住房。[NEWLINE]与营地相邻时+1 [ICON_Housing] 住房。[NEWLINE]与伊坎达相邻时+1 [ICON_Housing] 住房。（需“城市圈”游戏模式）[NEWLINE]与运河相邻时+1 [ICON_Housing] 住房。[NEWLINE]与航空港相邻时+1 [ICON_Housing] 住房。[NEWLINE]与市政广场相邻时+1 [ICON_Housing] 住房。[NEWLINE]与市中心相邻时+1 [ICON_Housing] 住房。[NEWLINE]与国家军营相邻时+3 [ICON_Housing] 住房。"		),
("en_US",	    "LOC_DISTRICT_IKANDA_DESCRIPTION",		"A district unique to Zulu which replaces the Encampmentattack range of defence is 2. Once the Civic or Technology prerequisite is met, Corps and Armies can be built outright. Faster Corps and Army creation. Buildings in the Ikanda receive +2 [ICON_GOLD] Gold and +1 [ICON_SCIENCE] Science. Can be built near city center, no appeal penalty.[NEWLINE][NEWLINE]+1 [ICON_Housing] Housing when adjacent to Farm.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Pasture.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Plantation.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Camp.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Ikanda.(Requries Megalopolis game mode)[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Canal.[NEWLINE]+1 [ICON_Housing] Housing when adjacent to Aerodrome.[NEWLINE]+3 [ICON_Housing] Housing when adjacent to National Encampment."	),
("zh_Hans_CN",	"LOC_TRAIT_CIVILIZATION_ZULU_ISIBONGO_DESCRIPTION",		"拥有驻军单位的城市每回合忠诚度+3；如驻扎有军团或军队，则+5。如已解锁相关市政，用单位占领城市则可将其升级为军团或军队。与牧场相邻的单元格+1 [ICON_FOOD] 食物。与种植园相邻的单元格+1 [ICON_Gold] 金币。"),
("en_US",	    "LOC_TRAIT_CIVILIZATION_ZULU_ISIBONGO_DESCRIPTION",		"Cities with a garrisoned unit get +3 Loyalty per turn, or +5 if it is a Corps or Army. Conquering a city with a unit will upgrade it into a Corps or Army, if the proper Civics are unlocked. +1 [ICON_FOOD] food for tile that is adjacent to Pasture. +1 [ICON_Gold] Gold for tile that is adjacent to Plantation."	),
("zh_Hans_CN",  "LOC_UNIT_ZULU_IMPI_DESCRIPTION",	                 	"祖鲁特色的中世纪抗骑兵单位，替代长矛兵，克制骑兵单位。视野为1。[NEWLINE][NEWLINE]增加夹击加成，费用和维护费较低。经验值增长速度较快。"),
("en_US",       "LOC_UNIT_ZULU_IMPI_DESCRIPTION",           "Zulu unique Medieval Era Anti-Cavalry unit that replaces the Pikeman, counters Cavalry units well. Sight: 1[NEWLINE][NEWLINE]Increased Flanking bonus, low cost and low maintenance. Earns XP faster.");